import React from 'react';
import InvoiceProcessor from './components/InvoiceProcessor';
import PeoplePage from './pages/PeoplePage';
import ClassificationPage from './pages/ClassificationPage';
import './App.css';

function App() {
  const [tab, setTab] = React.useState('invoices');
  return (
    <div className="App" style={{ padding: 16 }}>
      <div style={{ display: 'flex', gap: 8, marginBottom: 12 }}>
        <button onClick={() => setTab('invoices')}>Notas Fiscais</button>
        <button onClick={() => setTab('pessoas')}>Pessoas</button>
        <button onClick={() => setTab('classificacao')}>Classificação</button>
      </div>
      {tab === 'invoices' && <InvoiceProcessor />}
      {tab === 'pessoas' && <PeoplePage />}
      {tab === 'classificacao' && <ClassificationPage />}
    </div>
  );
}

export default App;
