import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  const [agentMessages, setAgentMessages] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  const addAgentMessage = (message) => {
    setAgentMessages(prev => [...prev, {
      id: Date.now() + Math.random().toString(36).substr(2, 9),
      texto: message,
      timestamp: new Date()
    }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    if (file.type !== 'application/pdf') {
      setError('Por favor, selecione apenas arquivos PDF');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);
    setAgentMessages([]);

    try {
      addAgentMessage('🚀 Iniciando processamento da nota fiscal...');
      addAgentMessage('📤 Enviando arquivo para o servidor...');
      
      const response = await processInvoice(file);
      
      console.log('=== RESPOSTA COMPLETA DO BACKEND ===');
      console.log('Response:', response);
      console.log('Response.success:', response?.success);
      console.log('Response.dadosExtraidos:', response?.dadosExtraidos);
      
      // Processar mensagens do agente
      if (response.processamento && response.processamento.mensagens) {
        console.log('📨 Mensagens do agente:', response.processamento.mensagens);
        setAgentMessages(response.processamento.mensagens);
        addAgentMessage('✅ Dados recebidos do backend');
      }

      // ✅ CORREÇÃO: Os dados estão em response.dadosExtraidos
      const dadosExtraidos = response.dadosExtraidos || response.data;
      
      console.log('=== DADOS EXTRAÍDOS PARA ANÁLISE ===');
      console.log('dadosExtraidos:', dadosExtraidos);
      
      // Validação se o Gemini não retornou dados
      if (!response || !response.success || !dadosExtraidos || isDataEmpty(dadosExtraidos)) {
        setError('Não foi possível extrair nenhum dado, NFE Inválida');
        setResult(null);
        addAgentMessage('❌ Nenhum dado válido encontrado na nota fiscal');
      } else {
        addAgentMessage('✅ Dados extraídos com sucesso!');
        
        setResult({
          success: true,
          data: dadosExtraidos,
          processamento: response.processamento,
          metadata: {
            fileName: file.name,
            processedAt: new Date().toISOString(),
            fonteDados: response.dadosExtraidos ? 'dadosExtraidos' : 'data'
          }
        });
      }
    } catch (err) {
      setError('Erro ao processar o arquivo. Verifique se o backend está rodando.');
      console.error('Erro:', err);
      addAgentMessage('❌ Erro de comunicação com o servidor');
    } finally {
      setLoading(false);
    }
  };

  // Função para verificar se os dados estão vazios
  const isDataEmpty = (data) => {
    if (!data) return true;
    
    const essentialFields = [
      data.fornecedor?.razaoSocial,
      data.fornecedor?.cnpj,
      data.cliente?.nome,
      data.cliente?.documento,
      data.numeroNotaFiscal,
      data.dataEmissao,
      data.valorTotal
    ];
    
    const allEmpty = essentialFields.every(field => 
      !field || field === 'N/A' || field === '' || field === 0
    );
    
    return allEmpty;
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value || 0);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A' || cnpj === '') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A' || cpf === '') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatEndereco = (endereco) => {
    if (!endereco || endereco === 'N/A' || endereco === '') return 'N/A';
    return endereco;
  };

  const formatDate = (dateString) => {
    if (!dateString || dateString === 'N/A' || dateString === '') return 'N/A';
    
    try {
      // Tenta converter a data para formato brasileiro
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return dateString;
      
      return date.toLocaleDateString('pt-BR');
    } catch {
      return dateString;
    }
  };

  const renderDocumentoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo === 'PF' && cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') {
      return formatCPF(cliente.cpf);
    } else if (cliente.tipo === 'PJ' && cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') {
      return formatCNPJ(cliente.cnpj);
    } else if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return formatCPF(doc);
      if (doc.length === 14) return formatCNPJ(doc);
      return cliente.documento;
    }
    
    return 'N/A';
  };

  // ✅ CORREÇÃO: Busca melhorada de produtos
  const getProdutos = (data) => {
    if (!data) return [];
    
    // Tenta várias localizações possíveis
    const produtos = data.produtos || data.itens || data.items || data.produtosServicos || [];
    
    // Se for um objeto, converte para array
    if (produtos && typeof produtos === 'object' && !Array.isArray(produtos)) {
      return Object.values(produtos);
    }
    
    return Array.isArray(produtos) ? produtos : [];
  };

  // ✅ CORREÇÃO: Busca melhorada de parcelas
  const getParcelas = (data) => {
    if (!data) return [];
    
    // Tenta várias localizações possíveis
    const parcelas = data.parcelas || data.parcela || data.pagamentos || data.condicaoPagamento || [];
    
    // Se for um objeto, converte para array
    if (parcelas && typeof parcelas === 'object' && !Array.isArray(parcelas)) {
      return Object.values(parcelas);
    }
    
    return Array.isArray(parcelas) ? parcelas : [];
  };

  // ✅ CORREÇÃO: Busca melhorada de classificação
  const getClassificacao = (data) => {
    if (!data) return 'Não classificada';
    
    const classificacao = data.classificacaoDespesa || data.categoria || data.tipoDespesa || data.classificacao;
    
    if (Array.isArray(classificacao)) {
      return classificacao.join(', ');
    }
    
    return classificacao || 'Não classificada';
  };

  // ✅ CORREÇÃO: Determina se é contas a pagar ou receber
  const getTipoMovimento = (data) => {
    if (!data) return 'N/A';
    
    // Verifica se tem informações que indicam o tipo
    const fornecedor = data.fornecedor || data.emitente;
    const cliente = data.cliente || data.destinatario;
    
    // Se tem fornecedor, geralmente é contas a pagar
    if (fornecedor && fornecedor.razaoSocial && fornecedor.razaoSocial !== 'N/A') {
      return 'Contas a Pagar';
    }
    
    // Se tem cliente, geralmente é contas a receber
    if (cliente && cliente.nome && cliente.nome !== 'N/A') {
      return 'Contas a Receber';
    }
    
    return 'N/A';
  };

  const renderProducts = (data) => {
    const produtos = getProdutos(data);
    
    if (produtos.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Serviços</h3>
          <p>Nenhum produto encontrado na nota fiscal</p>
          <details className="debug-details">
            <summary>Debug Produtos</summary>
            <pre>data.produtos: {JSON.stringify(data?.produtos, null, 2)}</pre>
            <pre>data.itens: {JSON.stringify(data?.itens, null, 2)}</pre>
          </details>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Serviços ({produtos.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Quantidade</th>
                <th>Valor Unitário</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || produto.nome || produto.descricaoProduto || 'Produto não especificado'}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                  <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (data) => {
    const parcelas = getParcelas(data);
    
    if (parcelas.length === 0) {
      return (
        <div className="parcelas-section">
          <h3>Parcelas</h3>
          <p>Nenhuma parcela encontrada</p>
          <details className="debug-details">
            <summary>Debug Parcelas</summary>
            <pre>data.parcelas: {JSON.stringify(data?.parcelas, null, 2)}</pre>
          </details>
        </div>
      );
    }

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{parcela.numeroParcela || parcela.parcela || index + 1}</td>
                  <td>{formatDate(parcela.dataVencimento || parcela.vencimento)}</td>
                  <td className="number">{formatCurrency(parcela.valor || parcela.valorParcela)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const getTipoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo) return cliente.tipo;
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') return 'PF';
    if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') return 'PJ';
    if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return 'PF';
      if (doc.length === 14) return 'PJ';
    }
    
    return 'N/A';
  };

  // ✅ CORREÇÃO: Componente para exibir mensagens do agente
  const AgentMessagesPanel = () => {
    if (agentMessages.length === 0) {
      return null;
    }

    return (
      <div className="agent-messages-panel">
        <h3>🤖 Agente Inteligente - Processamento</h3>
        <div className="agent-messages-container">
          {agentMessages.map((message, index) => {
            const uniqueKey = message.id ? `${message.id}_${index}` : `msg_${index}`;
            const messageText = message?.texto || '';
            const isError = messageText.includes('❌') || messageText.toLowerCase().includes('erro');
            const isSuccess = messageText.includes('✅') || messageText.toLowerCase().includes('sucesso');
            
            return (
              <div 
                key={uniqueKey}
                className={`agent-message ${isError ? 'error' : ''} ${isSuccess ? 'success' : ''}`}
              >
                <span className="agent-message-time">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString() : new Date().toLocaleTimeString()}
                </span>
                <div className="agent-message-text">
                  {messageText.split('\n').map((line, lineIndex) => (
                    <div key={`${uniqueKey}_line_${lineIndex}`}>{line}</div>
                  ))}
                </div>
              </div>
            );
          })}
          
          {loading && (
            <div className="agent-processing-indicator" key="processing_indicator">
              <div className="agent-spinner"></div>
              <span>Processando com Agente Inteligente...</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Faça upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      {/* Botão do Histórico */}
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          📋 Ver Histórico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {/* ✅ CORREÇÃO: Painel do Agente Inteligente */}
      <AgentMessagesPanel />

      {error && (
        <div className={`error-message ${error.includes('NFE Inválida') ? 'nfe-invalida' : ''}`}>
          {error}
        </div>
      )}

      {result && result.data && !isDataEmpty(result.data) && (
        <div className="result-container">
          <h2>
            Dados Extraídos da Nota Fiscal
            {result.metadata?.fromHistory && <span className="history-indicator">DO HISTÓRICO</span>}
          </h2>
          
          {/* ✅ CORREÇÃO: Indicador de fonte dos dados */}
          {result.metadata?.fonteDados && (
            <div className="data-source-indicator">
              <strong>✅ DADOS ENCONTRADOS!</strong> (Fonte: {result.metadata.fonteDados})
            </div>
          )}

          {/* Informações Básicas */}
          <div className={`summary-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>Informações da Nota</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">Número NF:</span>
                <span className="summary-value">{result.data.numeroNotaFiscal || result.data.numero || result.data.nf || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data Emissão:</span>
                <span className="summary-value">{formatDate(result.data.dataEmissao || result.data.data || result.data.emissao)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(result.data.valorTotal || result.data.total || result.data.valor)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Frete:</span>
                <span className="summary-value">{formatCurrency(result.data.valorFrete || result.data.frete || 0)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Parcelas:</span>
                <span className="summary-value">
                  {getParcelas(result.data).length || result.data.quantidadeParcelas || 0}
                </span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Tipo Movimento:</span>
                <span className="summary-value highlight">{getTipoMovimento(result.data)}</span>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          <div className={`supplier-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>
              Fornecedor (Emitente)
              {result.metadata?.fromHistory && <span className="history-indicator">DO HISTÓRICO</span>}
            </h3>
            <div className="supplier-info">
              <p><strong>Razão Social:</strong> <span className="info-value">{result.data.fornecedor?.razaoSocial || result.data.emitente?.razaoSocial || 'N/A'}</span></p>
              <p><strong>Nome Fantasia:</strong> <span className="info-value">{result.data.fornecedor?.fantasia || result.data.emitente?.fantasia || result.data.fornecedor?.nomeFantasia || 'N/A'}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(result.data.fornecedor?.cnpj || result.data.emitente?.cnpj) || 'N/A'}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{result.data.fornecedor?.tipo || 'PJ'}</span></p>
              <p><strong>Endereço:</strong> <span className="address-line">{formatEndereco(result.data.fornecedor?.endereco || result.data.emitente?.endereco)}</span></p>
            </div>
          </div>

          {/* Cliente - ✅ CORREÇÃO: Melhor busca de dados */}
          <div className={`client-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>Cliente (Destinatário)</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">
                {result.data.cliente?.nome || 
                 result.data.cliente?.razaoSocial || 
                 result.data.destinatario?.nome ||
                 result.data.destinatario?.razaoSocial || 
                 result.data.faturado?.nomeCompleto || 
                 'N/A'}
              </span></p>
              <p><strong>Documento:</strong> <span className="info-value">
                {renderDocumentoCliente(
                  result.data.cliente || 
                  result.data.destinatario || 
                  result.data.faturado
                )}
              </span></p>
              <p><strong>Tipo:</strong> <span className="info-value">
                {getTipoCliente(
                  result.data.cliente || 
                  result.data.destinatario || 
                  result.data.faturado
                )}
              </span></p>
              <p><strong>Endereço:</strong> <span className="address-line">
                {formatEndereco(
                  result.data.cliente?.endereco || 
                  result.data.destinatario?.endereco
                )}
              </span></p>
            </div>
          </div>

          {/* Produtos - ✅ CORREÇÃO: Busca em múltiplas localizações */}
          {renderProducts(result.data)}

          {/* Parcelas - ✅ CORREÇÃO: Busca em múltiplas localizações */}
          {renderParcelas(result.data)}

          {/* Classificação */}
          <div className="classification-section">
            <h3>Classificação da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> <span className="info-value">
                {getClassificacao(result.data)}
              </span></p>
              <p><strong>Tipo Movimento:</strong> <span className="info-value highlight">
                {getTipoMovimento(result.data)}
              </span></p>
            </div>
          </div>

          {/* JSON Completo (para debug) */}
          <details className="json-details">
            <summary>🔍 JSON Completo (Debug - Clique para ver todos os dados)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      {/* Painel de Histórico */}
      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
