import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  
  const [agentMessages, setAgentMessages] = useState([]);
  const [agentProcessing, setAgentProcessing] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  const addAgentMessage = (message) => {
    const newMessage = {
      id: Date.now() + Math.random(),
      texto: message || 'Mensagem sem conte√∫do',
      timestamp: new Date()
    };
    setAgentMessages(prev => [...prev, newMessage]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    if (file.type !== 'application/pdf') {
      setError('Por favor, selecione apenas arquivos PDF');
      return;
    }

    setLoading(true);
    setAgentProcessing(true);
    setError('');
    setAgentMessages([]);
    
    try {
      addAgentMessage('üöÄ Iniciando processamento da nota fiscal...');
      addAgentMessage('üìÑ Enviando arquivo para o servidor...');

      const response = await processInvoice(file);
      
      // DEBUG DETALHADO
      console.log('=== RESPOSTA COMPLETA DO BACKEND ===', response);
      console.log('Response.data:', response?.data);
      console.log('Response.dadosExtraidos:', response?.dadosExtraidos);
      console.log('Response.success:', response?.success);
      
      // Processar mensagens do agente se existirem
      if (response.processamento && response.processamento.mensagens) {
        const safeMessages = response.processamento.mensagens.map(msg => ({
          id: msg.id || Date.now() + Math.random(),
          texto: msg.texto || 'Mensagem sem conte√∫do',
          timestamp: msg.timestamp || new Date()
        }));
        setAgentMessages(safeMessages);
      }

      // CORRE√á√ÉO: Aceitar m√∫ltiplos formatos de resposta
      const extractedData = response?.data || response?.dadosExtraidos || response;
      
      console.log('=== DADOS EXTRA√çDOS PARA AN√ÅLISE ===', extractedData);
      console.log('Tem dados?', !!extractedData);
      console.log('isDataEmpty result:', isDataEmpty(extractedData));

      if (!extractedData || isDataEmpty(extractedData)) {
        console.log('Dados considerados vazios - mostrando erro');
        setError('N√£o foi poss√≠vel extrair dados suficientes da nota fiscal');
        setResult(null);
        addAgentMessage('‚ùå Dados insuficientes extra√≠dos da nota fiscal');
      } else {
        console.log('Dados v√°lidos - mostrando resultado');
        // CORRE√á√ÉO: Manter toda a estrutura da resposta
        setResult({
          ...response,
          data: extractedData // Garante compatibilidade
        });
        
        if (response.processamento && response.processamento.idMovimento) {
          addAgentMessage(`üéâ Processamento conclu√≠do! Movimento ID: ${response.processamento.idMovimento}`);
        } else {
          addAgentMessage('‚úÖ Dados extra√≠dos com sucesso!');
        }
      }
    } catch (err) {
      setError('Erro ao processar o arquivo. Verifique se o backend est√° rodando.');
      console.error('Erro:', err);
      addAgentMessage('‚ùå Erro na comunica√ß√£o com o servidor');
    } finally {
      setLoading(false);
      setAgentProcessing(false);
    }
  };

  // Fun√ß√£o de verifica√ß√£o MUITO mais flex√≠vel
  const isDataEmpty = (data) => {
    if (!data) return true;
    
    console.log('=== ANALISANDO DADOS NA ISDATAEMPTY ===', data);
    
    // Verifica se existe QUALQUER dado v√°lido
    const hasAnyData = 
      // Campos b√°sicos da nota
      (data.numeroNotaFiscal && data.numeroNotaFiscal !== 'N/A') ||
      (data.dataEmissao && data.dataEmissao !== 'N/A') ||
      (data.valorTotal !== undefined && data.valorTotal !== null && data.valorTotal !== 0) ||
      
      // Fornecedor
      (data.fornecedor && (
        data.fornecedor.razaoSocial ||
        data.fornecedor.nome || 
        data.fornecedor.cnpj ||
        data.fornecedor.documento
      )) ||
      
      // Cliente/Faturado
      (data.cliente && (
        data.cliente.nome ||
        data.cliente.documento ||
        data.cliente.cpf ||
        data.cliente.cnpj
      )) ||
      (data.faturado && (
        data.faturado.nome ||
        data.faturado.nomeCompleto ||
        data.faturado.documento
      )) ||
      
      // Produtos
      (data.produtos && Array.isArray(data.produtos) && data.produtos.length > 0) ||
      
      // Parcelas
      (data.parcelas && Array.isArray(data.parcelas) && data.parcelas.length > 0) ||
      
      // Outros campos poss√≠veis
      data.chaveAcesso ||
      data.serie ||
      data.modelo;

    console.log('Tem dados v√°lidos?', hasAnyData);
    
    return !hasAnyData;
  };

  const AgentMessagesPanel = () => {
    const messages = agentMessages || [];
    
    if (messages.length === 0 && !agentProcessing) {
      return null;
    }

    return (
      <div className="agent-messages-panel">
        <h3>ü§ñ Agente Inteligente - Processamento</h3>
        <div className="agent-messages-container">
          {messages.map((message) => {
            const messageText = message?.texto || 'Mensagem sem conte√∫do';
            const hasError = messageText.includes && messageText.includes('‚ùå');
            const hasSuccess = messageText.includes && messageText.includes('‚úÖ');
            
            return (
              <div key={message.id} className={`agent-message ${hasError ? 'error' : ''} ${hasSuccess ? 'success' : ''}`}>
                <span className="agent-message-time">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString() : new Date().toLocaleTimeString()}
                </span>
                <div className="agent-message-text">
                  {messageText.split('\n').map((line, index) => (
                    <div key={index}>{line}</div>
                  ))}
                </div>
              </div>
            );
          })}
          
          {agentProcessing && messages.length === 0 && (
            <div className="agent-message">
              <span className="agent-message-time">{new Date().toLocaleTimeString()}</span>
              <div className="agent-message-text">‚è≥ Iniciando processamento...</div>
            </div>
          )}
          
          {agentProcessing && (
            <div className="agent-processing-indicator">
              <div className="agent-spinner"></div>
              <span>Processando com Agente Inteligente...</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A' || cnpj === '') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A' || cpf === '') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatEndereco = (endereco) => {
    if (!endereco || endereco === 'N/A' || endereco === '') return 'N/A';
    return endereco;
  };

  const renderDocumentoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') {
      return formatCPF(cliente.cpf);
    } else if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') {
      return formatCNPJ(cliente.cnpj);
    } else if (cliente.documento && cliente.documento !== 'N/A' && cliente.documento !== '') {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return formatCPF(doc);
      if (doc.length === 14) return formatCNPJ(doc);
      return cliente.documento;
    }
    
    return 'N/A';
  };

  const renderProducts = (products) => {
    if (!products || !Array.isArray(products) || products.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Servi√ßos</h3>
          <p>Nenhum produto encontrado na nota fiscal</p>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Servi√ßos ({products.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descri√ß√£o</th>
                <th>Quantidade</th>
                <th>Valor Unit√°rio</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || 'Produto n√£o especificado'}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                  <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (parcelas) => {
    if (!parcelas || !Array.isArray(parcelas) || parcelas.length === 0) {
      return null;
    }

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{parcela.dataVencimento || 'N/A'}</td>
                  <td className="number">{formatCurrency(parcela.valor)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const getTipoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo) return cliente.tipo;
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') return 'PF';
    if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') return 'PJ';
    if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return 'PF';
      if (doc.length === 14) return 'PJ';
    }
    
    return 'N/A';
  };

  // NOVA FUN√á√ÉO: Renderizar informa√ß√µes b√°sicas de forma mais robusta
  const renderBasicInfo = (data) => {
    if (!data) return null;

    return (
      <div className="summary-section">
        <h3>Informa√ß√µes da Nota</h3>
        <div className="summary-grid">
          <div className="summary-item">
            <span className="summary-label">N√∫mero NF:</span>
            <span className="summary-value">{data.numeroNotaFiscal || 'N/A'}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Data Emiss√£o:</span>
            <span className="summary-value">{data.dataEmissao || 'N/A'}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Valor Total:</span>
            <span className="summary-value">{formatCurrency(data.valorTotal)}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Valor Frete:</span>
            <span className="summary-value">{formatCurrency(data.valorFrete)}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Chave Acesso:</span>
            <span className="summary-value small-text">{data.chaveAcesso || 'N/A'}</span>
          </div>
          {result?.processamento?.idMovimento && (
            <div className="summary-item">
              <span className="summary-label">ID Movimento:</span>
              <span className="summary-value agent-id">{result.processamento.idMovimento}</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Fa√ßa upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          üìã Ver Hist√≥rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      <AgentMessagesPanel />

      {error && (
        <div className={`error-message ${error.includes('NFE Inv√°lida') ? 'nfe-invalida' : ''}`}>
          {error}
        </div>
      )}

      {result && result.data && !isDataEmpty(result.data) && (
        <div className="result-container">
          <h2>
            Dados Extra√≠dos da Nota Fiscal
            {result.metadata?.fromHistory && <span className="history-indicator">DO HIST√ìRICO</span>}
            {result.processamento?.processadoPor && (
              <span className="agent-indicator">ü§ñ PROCESSADO POR AGENTE INTELIGENTE</span>
            )}
          </h2>
          
          {renderBasicInfo(result.data)}

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor (Emitente)</h3>
            <div className="supplier-info">
              <p><strong>Raz√£o Social:</strong> <span className="info-value">{result.data.fornecedor?.razaoSocial || result.data.fornecedor?.nome || 'N/A'}</span></p>
              <p><strong>Nome Fantasia:</strong> <span className="info-value">{result.data.fornecedor?.fantasia || 'N/A'}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(result.data.fornecedor?.cnpj || result.data.fornecedor?.documento) || 'N/A'}</span></p>
              <p><strong>Endere√ßo:</strong> <span className="address-line">{formatEndereco(result.data.fornecedor?.endereco)}</span></p>
            </div>
          </div>

          {/* Cliente */}
          <div className="client-section">
            <h3>Cliente (Destinat√°rio)</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">{result.data.cliente?.nome || result.data.faturado?.nome || result.data.faturado?.nomeCompleto || 'N/A'}</span></p>
              <p><strong>Documento:</strong> <span className="info-value">{renderDocumentoCliente(result.data.cliente || result.data.faturado)}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{getTipoCliente(result.data.cliente || result.data.faturado)}</span></p>
              <p><strong>Endere√ßo:</strong> <span className="address-line">{formatEndereco(result.data.cliente?.endereco || result.data.faturado?.endereco)}</span></p>
            </div>
          </div>

          {renderProducts(result.data.produtos)}
          {renderParcelas(result.data.parcelas)}

          {/* Classifica√ß√£o */}
          <div className="classification-section">
            <h3>Classifica√ß√£o da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> <span className="info-value">{result.data.classificacaoDespesa?.join(', ') || 'N√£o classificada'}</span></p>
            </div>
          </div>

          {/* JSON Completo para debug */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
