  { key: 'configuracoes', label: 'Configura√ß√µes', icon: '‚öôÔ∏è' },
];

export default function Sidebar({ active, onNavigate, user, onLogout }) {
  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <div className="user-avatar">GF</div>
        <div className="user-info">
          <div className="user-name">{user?.user || 'Usu√°rio'}</div>
          <div className="user-role">{user ? 'Autenticado' : 'Visitante'}</div>
        </div>
        <button className="logout-btn" title="Sair" onClick={onLogout}>‚éã</button>
      </div>
      <nav className="sidebar-menu">
        {menuItems.map((item) => (
          <button
