import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  const [agentMessages, setAgentMessages] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  const addAgentMessage = (message) => {
    setAgentMessages(prev => [...prev, {
      id: Date.now() + Math.random().toString(36).substr(2, 9),
      texto: message,
      timestamp: new Date()
    }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);
    setAgentMessages([]);

    try {
      addAgentMessage('🚀 Iniciando processamento da nota fiscal...');
      
      const response = await processInvoice(file);
      
      console.log('=== RESPOSTA COMPLETA DO BACKEND ===', response);

      // Processar mensagens do agente
      if (response.processamento && response.processamento.mensagens) {
        setAgentMessages(response.processamento.mensagens);
      }

      // Dados estão em response.dadosExtraidos
      const dadosExtraidos = response.dadosExtraidos || response.data;
      
      if (response.success && dadosExtraidos) {
        addAgentMessage('✅ Dados extraídos com sucesso!');
        
        setResult({
          success: true,
          data: dadosExtraidos,
          processamento: response.processamento,
          metadata: {
            fileName: file.name,
            processedAt: new Date().toISOString(),
            fonteDados: 'dadosExtraidos'
          }
        });
      } else {
        setError('Não foi possível extrair dados da nota fiscal');
        addAgentMessage('❌ Falha na extração de dados');
      }
      
    } catch (err) {
      setError('Erro ao processar o arquivo');
      console.error('Erro:', err);
      addAgentMessage('❌ Erro de comunicação com o servidor');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatDate = (dateString) => {
    if (!dateString || dateString === 'N/A') return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('pt-BR');
    } catch {
      return dateString;
    }
  };

  // Componente para exibir qualquer objeto de forma organizada
  const renderObjectSection = (title, obj, excludeFields = []) => {
    if (!obj || typeof obj !== 'object') return null;

    const fields = Object.entries(obj).filter(([key]) => !excludeFields.includes(key));

    if (fields.length === 0) return null;

    return (
      <div className="data-section">
        <h3>{title}</h3>
        <div className="data-grid">
          {fields.map(([key, value]) => (
            <div key={key} className="data-item">
              <span className="data-label">{key}:</span>
              <span className="data-value">
                {typeof value === 'object' ? JSON.stringify(value) : 
                 String(value || 'N/A')}
              </span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Componente para produtos
  const renderProducts = (products) => {
    if (!products || !Array.isArray(products) || products.length === 0) {
      return (
        <div className="data-section">
          <h3>📦 Produtos/Serviços</h3>
          <p>Nenhum produto extraído</p>
        </div>
      );
    }

    return (
      <div className="data-section">
        <h3>📦 Produtos/Serviços ({products.length})</h3>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Quantidade</th>
                <th>Valor Unit.</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || produto.nome || 'N/A'}</td>
                  <td className="number">{produto.quantidade || '0'}</td>
                  <td className="number">{formatCurrency(produto.valorUnitario)}</td>
                  <td className="number">{formatCurrency(produto.valorTotal)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  // Componente para parcelas
  const renderParcelas = (parcelas) => {
    if (!parcelas) return null;

    // Se parcelas é um número
    if (typeof parcelas === 'number') {
      return (
        <div className="data-section">
          <h3>📅 Condição de Pagamento</h3>
          <div className="data-grid">
            <div className="data-item">
              <span className="data-label">Quantidade de Parcelas:</span>
              <span className="data-value">{parcelas}</span>
            </div>
          </div>
        </div>
      );
    }

    // Se parcelas é um array
    if (Array.isArray(parcelas) && parcelas.length > 0) {
      return (
        <div className="data-section">
          <h3>📅 Parcelas ({parcelas.length})</h3>
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Parcela</th>
                  <th>Vencimento</th>
                  <th>Valor</th>
                </tr>
              </thead>
              <tbody>
                {parcelas.map((parcela, index) => (
                  <tr key={index}>
                    <td>{parcela.numeroParcela || index + 1}</td>
                    <td>{formatDate(parcela.dataVencimento)}</td>
                    <td className="number">{formatCurrency(parcela.valor)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    }

    return null;
  };

  // Componente para mensagens do agente
  const AgentMessagesPanel = () => {
    if (agentMessages.length === 0) return null;

    return (
      <div className="agent-messages-panel">
        <h3>🤖 Log do Processamento</h3>
        <div className="agent-messages-container">
          {agentMessages.map((message, index) => (
            <div key={index} className="agent-message">
              <span className="agent-time">
                {new Date(message.timestamp).toLocaleTimeString()}
              </span>
              <div className="agent-text">
                {message.texto}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Faça upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          📋 Ver Histórico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      <AgentMessagesPanel />

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {result && result.data && (
        <div className="result-container">
          <h2>🎯 Dados Extraídos da Nota Fiscal</h2>
          
          {/* Informações Principais */}
          <div className="main-info-section">
            <h3>📋 Informações Principais</h3>
            <div className="main-info-grid">
              <div className="info-card">
                <h4>Número da NF</h4>
                <p className="info-value-large">{result.data.numeroNotaFiscal || result.data.numero || 'N/A'}</p>
              </div>
              <div className="info-card">
                <h4>Data Emissão</h4>
                <p className="info-value-large">{formatDate(result.data.dataEmissao || result.data.data)}</p>
              </div>
              <div className="info-card">
                <h4>Valor Total</h4>
                <p className="info-value-large highlight">{formatCurrency(result.data.valorTotal)}</p>
              </div>
              <div className="info-card">
                <h4>Tipo</h4>
                <p className="info-value-large">{result.data.tipoMovimento || 'Contas a Pagar'}</p>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          {renderObjectSection('🏢 Fornecedor', result.data.fornecedor)}

          {/* Cliente/Destinatário */}
          {renderObjectSection('👤 Cliente/Destinatário', 
            result.data.cliente || result.data.destinatario || result.data.faturado
          )}

          {/* Produtos */}
          {renderProducts(result.data.produtos || result.data.itens)}

          {/* Parcelas */}
          {renderParcelas(result.data.parcelas)}

          {/* Impostos */}
          {renderObjectSection('💰 Impostos', result.data.impostos)}

          {/* Transporte */}
          {renderObjectSection('🚚 Transporte', result.data.transporte)}

          {/* Informações Adicionais */}
          {renderObjectSection('📄 Informações Adicionais', result.data, [
            'fornecedor', 'cliente', 'destinatario', 'faturado', 
            'produtos', 'itens', 'parcelas', 'impostos', 'transporte'
          ])}

          {/* Dados do Processamento */}
          {result.processamento && (
            <div className="data-section">
              <h3>⚙️ Dados do Processamento</h3>
              <div className="data-grid">
                <div className="data-item">
                  <span className="data-label">ID Movimento:</span>
                  <span className="data-value">{result.processamento.idMovimento || 'N/A'}</span>
                </div>
                <div className="data-item">
                  <span className="data-label">Status:</span>
                  <span className="data-value">{result.processamento.status || 'N/A'}</span>
                </div>
              </div>
            </div>
          )}

          {/* JSON Completo */}
          <details className="json-details">
            <summary>🔍 JSON Completo (Todos os dados recebidos)</summary>
            <pre className="result-json">
              {JSON.stringify(result.data, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
