import React from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import TransactionsPage from './pages/TransactionsPage';
import PeoplePage from './pages/PeoplePage';
import ClassificationPage from './pages/ClassificationPage';

function App() {
  const [menu, setMenu] = React.useState('transacoes');

  const renderContent = () => {
    switch (menu) {
      case 'transacoes':
        return <TransactionsPage />;
      case 'contas_pagar':
        return <div className="card"><h2>Contas a Pagar</h2><p>Em breve: listagem e ações.</p></div>;
      case 'contas_receber':
        return <div className="card"><h2>Contas a Receber</h2><p>Em breve: listagem e ações.</p></div>;
      case 'fluxo_caixa':
        return <div className="card"><h2>Fluxo de Caixa</h2><p>Em breve: gráfico e indicadores.</p></div>;
      case 'categorias':
        return <ClassificationPage />;
      case 'clientes':
        return <PeoplePage />;
      case 'fornecedores':
        return <PeoplePage />;
      case 'relatorios':
        return <div className="card"><h2>Relatórios</h2><p>Em breve: exportação e filtros.</p></div>;
      case 'configuracoes':
        return <div className="card"><h2>Configurações</h2><p>Em breve: preferências e integrações.</p></div>;
      default:
        return <TransactionsPage />;
    }
  };

  return (
    <div className="layout">
      <Sidebar active={menu} onNavigate={setMenu} />
      <main className="content">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
