import React, { useState, useEffect } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  
  const [agentMessages, setAgentMessages] = useState([]);
  const [agentProcessing, setAgentProcessing] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  const addAgentMessage = (message) => {
    setAgentMessages(prev => [...prev, {
      id: Date.now() + Math.random().toString(36).substr(2, 9),
      texto: message,
      timestamp: new Date()
    }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setAgentProcessing(true);
    setError('');
    setResult(null);
    setAgentMessages([]);

    try {
      addAgentMessage('🚀 Iniciando processamento da nota fiscal...');
      
      console.log('📤 Enviando arquivo para backend...');
      const response = await processInvoice(file);
      
      console.log('=== 🎯 RESPONSE ===');
      console.log('Response:', response);
      console.log('Response.success:', response?.success);
      console.log('Response.dadosExtraidos:', response?.dadosExtraidos);
      console.log('=== FIM DEBUG ===');

      // Processar mensagens do agente
      if (response.processamento && response.processamento.mensagens) {
        console.log('📨 Mensagens do agente:', response.processamento.mensagens);
        setAgentMessages(response.processamento.mensagens);
      }

      if (response.success) {
        // CORREÇÃO: OS DADOS ESTÃO EM response.dadosExtraidos!
        let dadosParaExibir = null;
        let fonte = '';
        
        if (response.dadosExtraidos && typeof response.dadosExtraidos === 'object') {
          console.log('✅ Dados encontrados em response.dadosExtraidos');
          dadosParaExibir = response.dadosExtraidos;
          fonte = 'response.dadosExtraidos';
        } else if (response.data && typeof response.data === 'object') {
          console.log('✅ Dados encontrados em response.data');
          dadosParaExibir = response.data;
          fonte = 'response.data';
        } else if (response.dadosNota && typeof response.dadosNota === 'object') {
          console.log('✅ Dados encontrados em response.dadosNota');
          dadosParaExibir = response.dadosNota;
          fonte = 'response.dadosNota';
        } else {
          console.log('❌ Nenhum dado encontrado');
        }
        
        console.log('🎯 Dados para exibir:', dadosParaExibir);
        
        if (dadosParaExibir) {
          const resultData = {
            success: true,
            data: dadosParaExibir,
            processamento: response.processamento,
            metadata: {
              fileName: file.name,
              processedAt: new Date().toISOString(),
              fonteDados: fonte
            }
          };
          
          console.log('✅ Definindo result:', resultData);
          setResult(resultData);
          addAgentMessage('✅ Processamento concluído com sucesso!');
        } else {
          setError('Não foi possível localizar os dados extraídos');
          addAgentMessage('❌ Estrutura de resposta não reconhecida');
        }
      } else {
        setError(response?.error || 'Erro desconhecido no processamento');
        addAgentMessage('❌ Erro no processamento');
      }
      
    } catch (err) {
      console.error('💥 Erro no frontend:', err);
      setError('Erro ao processar o arquivo');
      addAgentMessage('❌ Erro de comunicação com o servidor');
    } finally {
      setLoading(false);
      setAgentProcessing(false);
    }
  };

  // Componente para exibir mensagens do agente
  const AgentMessagesPanel = () => {
    if (agentMessages.length === 0 && !agentProcessing) {
      return null;
    }

    return (
      <div className="agent-messages-panel">
        <h3>🤖 Agente Inteligente - Processamento</h3>
        <div className="agent-messages-container">
          {agentMessages.map((message, index) => {
            const uniqueKey = message.id ? `${message.id}_${index}` : `msg_${index}`;
            const messageText = message?.texto || '';
            const isError = messageText.includes('❌') || messageText.toLowerCase().includes('erro');
            const isSuccess = messageText.includes('✅') || messageText.toLowerCase().includes('sucesso');
            
            return (
              <div 
                key={uniqueKey}
                className={`agent-message ${isError ? 'error' : ''} ${isSuccess ? 'success' : ''}`}
              >
                <span className="agent-message-time">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString() : new Date().toLocaleTimeString()}
                </span>
                <div className="agent-message-text">
                  {messageText.split('\n').map((line, lineIndex) => (
                    <div key={`${uniqueKey}_line_${lineIndex}`}>{line}</div>
                  ))}
                </div>
              </div>
            );
          })}
          
          {agentProcessing && agentMessages.length === 0 && (
            <div className="agent-message" key="processing_initial">
              <span className="agent-message-time">{new Date().toLocaleTimeString()}</span>
              <div className="agent-message-text">⏳ Iniciando processamento...</div>
            </div>
          )}
          
          {agentProcessing && (
            <div className="agent-processing-indicator" key="processing_indicator">
              <div className="agent-spinner"></div>
              <span>Processando com Agente Inteligente...</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  // Funções de formatação
  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A' || cnpj === '') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A' || cpf === '') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatEndereco = (endereco) => {
    if (!endereco || endereco === 'N/A' || endereco === '') return 'N/A';
    return endereco;
  };

  // RENDERIZAÇÃO CORRIGIDA
  const renderData = () => {
    if (!result || !result.data) {
      return (
        <div className="error-message">
          <h3>❌ Nenhum dado disponível</h3>
          <p>O processamento foi concluído, mas nenhum dado foi recebido para exibição.</p>
        </div>
      );
    }

    console.log('🎯 Renderizando com result.data:', result.data);

    // Extrair dados diretamente - agora sabemos que estão em result.data
    const dados = result.data;
    
    const fornecedor = dados.fornecedor;
    const cliente = dados.cliente || dados.faturado;
    const numeroNotaFiscal = dados.numeroNotaFiscal;
    const dataEmissao = dados.dataEmissao;
    const valorTotal = dados.valorTotal;
    const produtos = dados.produtos;
    const parcelas = dados.parcelas;
    const classificacaoDespesa = dados.classificacaoDespesa;

    console.log('📊 Dados extraídos:');
    console.log('- Fornecedor:', fornecedor);
    console.log('- Cliente:', cliente);
    console.log('- Número NF:', numeroNotaFiscal);
    console.log('- Valor Total:', valorTotal);

    return (
      <div className="result-container">
        <h2>📄 Dados Extraídos da Nota Fiscal</h2>
        
        {/* INFO DA FONTE */}
        {result.metadata?.fonteDados && (
          <div style={{
            background: '#e8f5e8', 
            padding: '10px', 
            margin: '10px 0', 
            border: '2px solid #4caf50',
            borderRadius: '8px'
          }}>
            <strong>✅ DADOS ENCONTRADOS!</strong> (Fonte: {result.metadata.fonteDados})
          </div>
        )}

        {/* Informações Básicas */}
        <div className="summary-section">
          <h3>📋 Informações da Nota</h3>
          <div className="summary-grid">
            <div className="summary-item">
              <span className="summary-label">Número NF:</span>
              <span className="summary-value">{numeroNotaFiscal || 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Data Emissão:</span>
              <span className="summary-value">{dataEmissao || 'N/A'}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Valor Total:</span>
              <span className="summary-value">{formatCurrency(valorTotal)}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Valor Frete:</span>
              <span className="summary-value">{formatCurrency(dados.valorFrete || 0)}</span>
            </div>
            {result.processamento?.idMovimento && (
              <div className="summary-item">
                <span className="summary-label">ID Movimento:</span>
                <span className="summary-value" style={{color: '#4caf50', fontWeight: 'bold'}}>
                  #{result.processamento.idMovimento}
                </span>
              </div>
            )}
          </div>
        </div>

        {/* Fornecedor */}
        {fornecedor && (
          <div className="supplier-section">
            <h3>🏢 Fornecedor (Emitente)</h3>
            <div className="supplier-info">
              <p><strong>Razão Social:</strong> <span className="info-value">{fornecedor.razaoSocial || 'N/A'}</span></p>
              <p><strong>Nome Fantasia:</strong> <span className="info-value">{fornecedor.fantasia || 'N/A'}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(fornecedor.cnpj)}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{fornecedor.tipo || 'PJ'}</span></p>
              <p><strong>Endereço:</strong> <span className="address-line">{formatEndereco(fornecedor.endereco)}</span></p>
            </div>
          </div>
        )}

        {/* Cliente */}
        {cliente && (
          <div className="client-section">
            <h3>👤 Cliente (Destinatário)</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">{cliente.nome || cliente.nomeCompleto || 'N/A'}</span></p>
              <p><strong>Documento:</strong> <span className="info-value">
                {cliente.cpf ? formatCPF(cliente.cpf) : 
                 cliente.cnpj ? formatCNPJ(cliente.cnpj) :
                 cliente.documento || 'N/A'}
              </span></p>
              <p><strong>Tipo:</strong> <span className="info-value">
                {cliente.tipo || (cliente.cpf ? 'PF' : cliente.cnpj ? 'PJ' : 'N/A')}
              </span></p>
              <p><strong>Endereço:</strong> <span className="address-line">{formatEndereco(cliente.endereco)}</span></p>
            </div>
          </div>
        )}

        {/* Produtos */}
        {produtos && Array.isArray(produtos) && produtos.length > 0 ? (
          <div className="products-section">
            <h3>📦 Produtos/Serviços ({produtos.length})</h3>
            <div className="products-table">
              <table>
                <thead>
                  <tr>
                    <th>Descrição</th>
                    <th>Quantidade</th>
                    <th>Valor Unitário</th>
                    <th>Valor Total</th>
                  </tr>
                </thead>
                <tbody>
                  {produtos.map((produto, index) => (
                    <tr key={index}>
                      <td>{produto.descricao || 'Produto não especificado'}</td>
                      <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                      <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                      <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : (
          <div className="products-section">
            <h3>📦 Produtos/Serviços</h3>
            <p>Nenhum produto encontrado na nota fiscal</p>
          </div>
        )}

        {/* Parcelas */}
        {parcelas && Array.isArray(parcelas) && parcelas.length > 0 && (
          <div className="parcelas-section">
            <h3>📅 Parcelas ({parcelas.length})</h3>
            <div className="parcelas-table">
              <table>
                <thead>
                  <tr>
                    <th>Parcela</th>
                    <th>Vencimento</th>
                    <th>Valor</th>
                  </tr>
                </thead>
                <tbody>
                  {parcelas.map((parcela, index) => (
                    <tr key={index}>
                      <td>{parcela.numeroParcela || index + 1}</td>
                      <td>{parcela.dataVencimento || 'N/A'}</td>
                      <td className="number">{formatCurrency(parcela.valor)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Classificação */}
        {classificacaoDespesa && (
          <div className="classification-section">
            <h3>📊 Classificação da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> <span className="info-value">
                {Array.isArray(classificacaoDespesa) 
                  ? classificacaoDespesa.join(', ') 
                  : String(classificacaoDespesa)}
              </span></p>
            </div>
          </div>
        )}

        {/* JSON Completo para Debug */}
        <details className="json-details">
          <summary>🔧 JSON Completo (Debug)</summary>
          <pre className="result-json">
            {JSON.stringify(result.data, null, 2)}
          </pre>
        </details>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Faça upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          📋 Ver Histórico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      <AgentMessagesPanel />

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {/* Renderização dos dados */}
      {result && result.success && renderData()}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
