// ... (código anterior mantido)

const renderDocumentoCliente = (cliente) => {
  if (!cliente || !cliente.documento || cliente.documento === 'N/A' || cliente.documento === '') {
    return 'N/A';
  }
  
  // Formatar baseado no tipo determinado pelo BACKEND (não pelo frontend)
  if (cliente.tipo === 'PF') {
    return formatCPF(cliente.documento);
  } else if (cliente.tipo === 'PJ') {
    return formatCNPJ(cliente.documento);
  }
  
  return cliente.documento; // Se não souber o tipo, mostra o número cru
};

