import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      console.log('🔄 Enviando arquivo para processamento...');
      const response = await processInvoice(file);
      
      console.log('✅ RESPOSTA DO BACKEND:', response);

      if (response && response.success) {
        console.log('✅ Processamento concluído com sucesso');
        setResult(response);
      } else {
        setError('Não foi possível processar a nota fiscal');
      }
    } catch (err) {
      console.error('❌ Erro no processamento:', err);
      setError('Erro ao processar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  // CORRIGIDO: Acessar produtos da estrutura correta
  const getProducts = () => {
    if (!result?.data?.produtos || !Array.isArray(result.data.produtos)) {
      console.log('❌ Nenhum produto encontrado na resposta:', result?.data);
      return [];
    }
    
    console.log('🛍️ PRODUTOS DA RESPOSTA:', result.data.produtos);
    return result.data.produtos;
  };

  // CORRIGIDO: Acessar parcelas da estrutura correta
  const getParcelas = () => {
    if (!result?.data?.parcelas || !Array.isArray(result.data.parcelas)) {
      console.log('❌ Nenhuma parcela encontrada na resposta:', result?.data);
      return [];
    }
    
    console.log('📅 PARCELAS DA RESPOSTA:', result.data.parcelas);
    return result.data.parcelas;
  };

  // CORRIGIDO: Acessar informações da nota fiscal corretamente
  const getInvoiceInfo = () => {
    if (!result?.data) {
      return {
        numero: 'N/A',
        valorTotal: 0,
        dataEmissao: 'N/A'
      };
    }
    
    // Tentar diferentes possíveis campos para número da nota
    const numero = result.data.numero || 
                  result.data.numeroNota || 
                  result.data.numeroNf || 
                  'N/A';
    
    // Tentar diferentes possíveis campos para valor total
    const valorTotal = result.data.valorTotal || 
                      result.data.total || 
                      result.data.valor || 
                      0;
    
    // Tentar diferentes possíveis campos para data
    const dataEmissao = result.data.dataEmissao || 
                       result.data.emissao || 
                       result.data.data || 
                       'N/A';
    
    return {
      numero,
      valorTotal,
      dataEmissao
    };
  };

  // CORRIGIDO: Acessar informações do fornecedor corretamente
  const getSupplierInfo = () => {
    // Primeiro tenta acessar fornecedor diretamente, depois tenta outras estruturas
    const fornecedorData = result?.data?.fornecedor || 
                          result?.data?.emitente || 
                          result?.fornecedor;
    
    if (!fornecedorData) {
      console.log('❌ Nenhum dado de fornecedor encontrado:', result?.data);
      return {
        razaoSocial: 'N/A',
        cnpj: 'N/A',
        status: 'N/A'
      };
    }
    
    const razaoSocial = fornecedorData.razaoSocial || 
                       fornecedorData.nome || 
                       fornecedorData.razao_social ||
                       'N/A';
    
    const cnpj = fornecedorData.cnpj || 
                fornecedorData.documento || 
                fornecedorData.cnpj_cpf ||
                'N/A';
    
    const status = fornecedorData.status || 'Cadastrado';
    
    return {
      razaoSocial,
      cnpj,
      status
    };
  };

  // CORRIGIDO: Acessar informações do cliente/faturado corretamente
  const getClientInfo = () => {
    // Primeiro tenta acessar faturado diretamente, depois tenta outras estruturas
    const clienteData = result?.data?.faturado || 
                       result?.data?.cliente || 
                       result?.data?.destinatario ||
                       result?.faturado;
    
    if (!clienteData) {
      console.log('❌ Nenhum dado de cliente encontrado:', result?.data);
      return {
        nome: 'N/A',
        documento: 'N/A',
        status: 'N/A'
      };
    }
    
    const nome = clienteData.nome || 
                clienteData.razaoSocial || 
                clienteData.razao_social ||
                'N/A';
    
    const documento = clienteData.documento || 
                     clienteData.cpf || 
                     clienteData.cnpj || 
                     clienteData.cpf_cnpj ||
                     'N/A';
    
    const status = clienteData.status || 'Cadastrado';
    
    return {
      nome,
      documento,
      status
    };
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatDate = (dateString) => {
    if (!dateString || dateString === 'N/A') return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('pt-BR');
    } catch {
      return dateString;
    }
  };

  const renderProducts = () => {
    const produtos = getProducts();
    
    console.log('🎯 PRODUTOS PARA RENDERIZAR:', produtos);

    if (produtos.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Serviços</h3>
          <div className="info-banner">
            <p>⚠️ <strong>Informação:</strong> Nenhum produto encontrado na resposta.</p>
            <p>Verifique se o backend está retornando os produtos corretamente.</p>
            <details>
              <summary>Ver dados recebidos</summary>
              <pre>{JSON.stringify(result?.data, null, 2)}</pre>
            </details>
          </div>
        </div>
      );
    }

    const totalProdutos = produtos.reduce((sum, produto) => sum + (produto.valorTotal || produto.valor || 0), 0);
    const invoiceInfo = getInvoiceInfo();

    return (
      <div className="products-section">
        <h3>Produtos/Serviços ({produtos.length})</h3>
        
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Classificação</th>
                <th>Quantidade</th>
                <th>Valor Unitário</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || produto.nome || 'Produto sem descrição'}</td>
                  <td className="classification">{produto.classificacao || "NÃO CLASSIFICADO"}</td>
                  <td className="number">{produto.quantidade?.toFixed(2) || '0.00'}</td>
                  <td className="number">{formatCurrency(produto.valorUnitario || produto.unitario)}</td>
                  <td className="number">{formatCurrency(produto.valorTotal || produto.valor)}</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="4" className="total-label"><strong>Soma dos Produtos:</strong></td>
                <td className="number">{formatCurrency(totalProdutos)}</td>
              </tr>
              <tr>
                <td colSpan="4" className="total-label"><strong>Valor Total da Nota:</strong></td>
                <td className="number total-value">{formatCurrency(invoiceInfo.valorTotal)}</td>
              </tr>
              {Math.abs(totalProdutos - invoiceInfo.valorTotal) > 0.01 && (
                <tr>
                  <td colSpan="4" className="total-label"><strong>Diferença:</strong></td>
                  <td className="number difference-value">
                    {formatCurrency(totalProdutos - invoiceInfo.valorTotal)}
                  </td>
                </tr>
              )}
            </tfoot>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = () => {
    const parcelas = getParcelas();
    
    if (parcelas.length === 0) {
      return (
        <div className="parcelas-section">
          <h3>Parcelas</h3>
          <div className="info-banner">
            <p>ℹ️ <strong>Informação:</strong> Nenhuma parcela encontrada na resposta.</p>
          </div>
        </div>
      );
    }

    const totalParcelas = parcelas.reduce((sum, parcela) => sum + (parcela.valor || 0), 0);

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Valor</th>
                <th>Vencimento</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td className="number">{parcela.numero || parcela.parcela || index + 1}</td>
                  <td className="number">{formatCurrency(parcela.valor)}</td>
                  <td className="date">{formatDate(parcela.vencimento || parcela.dataVencimento || parcela.data)}</td>
                  <td className="status">
                    <span className="status-pending">Pendente</span>
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="3" className="total-label"><strong>Total das Parcelas:</strong></td>
                <td className="number total-value">{formatCurrency(totalParcelas)}</td>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>
    );
  };

  const renderClassification = () => {
    const produtos = getProducts();
    const classificacoes = [...new Set(produtos.map(p => p.classificacao).filter(Boolean))];
    
    return (
      <div className="classification-section">
        <h3>Classificação da Despesa</h3>
        <div className="classification-info">
          <p><strong>Categoria:</strong> <span className="info-value">
            {classificacoes.length > 0 ? classificacoes.join(', ') : 'NÃO CLASSIFICADO'}
          </span></p>
        </div>
      </div>
    );
  };

  const renderProcessamentoInfo = () => {
    if (!result?.processamento) return null;

    return (
      <div className="processamento-section">
        <h3>Processamento do Agente</h3>
        <div className="processamento-info">
          <p><strong>Status:</strong> <span className="info-value success">{result.processamento.resumo}</span></p>
          <p><strong>ID do Movimento:</strong> <span className="info-value">{result.processamento.etapas?.movimento?.replace('CRIADO - ID: ', '') || 'N/A'}</span></p>
          
          <div className="etapas">
            <h4>Etapas do Processamento:</h4>
            <ul>
              {result.processamento.etapas?.fornecedor && <li>✅ {result.processamento.etapas.fornecedor}</li>}
              {result.processamento.etapas?.faturado && <li>✅ {result.processamento.etapas.faturado}</li>}
              {result.processamento.etapas?.despesa && <li>✅ {result.processamento.etapas.despesa}</li>}
              {result.processamento.etapas?.movimento && <li>✅ {result.processamento.etapas.movimento}</li>}
            </ul>
          </div>
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
  };

  const handleSelectHistoryNote = (noteData) => {
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: { fromHistory: true }
      });
    }
  };

  // Render principal
  const invoiceInfo = getInvoiceInfo();
  const supplierInfo = getSupplierInfo();
  const clientInfo = getClientInfo();

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Faça upload de uma nota fiscal em PDF</p>
      
      <div className="history-button-container">
        <button type="button" onClick={handleShowHistory} className="history-button">
          📋 Ver Histórico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button type="submit" disabled={loading || !file} className="process-button">
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {result && result.success && (
        <div className="result-container">
          <h2>✅ Processamento Concluído com Sucesso!</h2>
          
          {renderProcessamentoInfo()}

          {/* Informações Básicas */}
          <div className="summary-section">
            <h3>Informações da Nota Fiscal</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">Número NF:</span>
                <span className="summary-value">{invoiceInfo.numero}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(invoiceInfo.valorTotal)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data Emissão:</span>
                <span className="summary-value">{formatDate(invoiceInfo.dataEmissao)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Status:</span>
                <span className="summary-value success">Processada com sucesso</span>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor</h3>
            <div className="supplier-info">
              <p><strong>Razão Social:</strong> <span className="info-value">{supplierInfo.razaoSocial}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(supplierInfo.cnpj)}</span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">✓ {supplierInfo.status}</span></p>
            </div>
          </div>

          {/* Cliente/Faturado */}
          <div className="client-section">
            <h3>Cliente/Faturado</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">{clientInfo.nome}</span></p>
              <p><strong>CPF/CNPJ:</strong> <span className="info-value">
                {clientInfo.documento?.length === 11 ? formatCPF(clientInfo.documento) : formatCNPJ(clientInfo.documento)}
              </span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">✓ {clientInfo.status}</span></p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts()}

          {/* Parcelas */}
          {renderParcelas()}

          {/* Classificação */}
          {renderClassification()}

          {/* Mensagens do Processamento */}
          {result.processamento?.mensagens && (
            <div className="messages-section">
              <h3>Detalhes do Processamento</h3>
              <div className="messages-container">
                {result.processamento.mensagens.map((msg, index) => (
                  <div key={index} className="message-item">
                    <span className="message-time">•</span>
                    <span className="message-text">{typeof msg === 'string' ? msg : msg.texto || ''}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Debug - MOSTRAR TODOS OS DADOS RECEBIDOS */}
          <details className="json-details">
            <summary>JSON Completo (Debug - Verifique aqui a estrutura real)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
