import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      console.log('üîÑ Enviando arquivo para processamento...');
      const response = await processInvoice(file);
      
      console.log('‚úÖ RESPOSTA DO BACKEND COMPLETA:', response);
      console.log('üìä Response.data:', response?.data);
      console.log('üì¶ Response.dadosExtraidos:', response?.dadosExtraidos);
      console.log('üîç Response.keys:', Object.keys(response || {}));
      
      // Verificar TODOS os n√≠veis poss√≠veis
      if (response?.data) {
        console.log('üéØ Dados em response.data:', response.data);
        console.log('üîç response.data keys:', Object.keys(response.data));
        console.log('üõçÔ∏è Produtos em data:', response.data.produtos);
        console.log('üè∑Ô∏è Classifica√ß√£o em data:', response.data.classificacaoDespesa);
      }
      
      if (response?.dadosExtraidos) {
        console.log('üéØ Dados em response.dadosExtraidos:', response.dadosExtraidos);
        console.log('üîç dadosExtraidos keys:', Object.keys(response.dadosExtraidos));
        console.log('üõçÔ∏è Produtos em dadosExtraidos:', response.dadosExtraidos.produtos);
        console.log('üè∑Ô∏è Classifica√ß√£o em dadosExtraidos:', response.dadosExtraidos.classificacaoDespesa);
      }

      // Aceitar QUALQUER resposta que tenha dados
      if (response && (response.data || response.dadosExtraidos || response.produtos || response.fornecedor)) {
        console.log('‚úÖ Dados v√°lidos encontrados, configurando resultado...');
        setResult(response);
      } else {
        console.log('‚ùå Nenhum dado v√°lido encontrado');
        setError('N√£o foi poss√≠vel extrair dados da nota fiscal');
      }
    } catch (err) {
      console.error('‚ùå Erro no processamento:', err);
      setError('Erro ao processar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  // Fun√ß√£o auxiliar simples para pegar dados
  const getData = () => {
    if (!result) return null;
    return result.data || result.dadosExtraidos || result;
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj) return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const renderProducts = () => {
    const data = getData();
    if (!data) return null;

    console.log('üîç Buscando produtos em:', data);
    
    // Tentar encontrar produtos em v√°rios lugares
    const produtos = data.produtos || data.itens || data.items || [];
    
    console.log('üõçÔ∏è Produtos encontrados:', produtos);

    if (!Array.isArray(produtos) || produtos.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Servi√ßos</h3>
          <p>Nenhum produto encontrado</p>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Servi√ßos ({produtos.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descri√ß√£o</th>
                <th>Quantidade</th>
                <th>Valor Unit√°rio</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || produto.nome || 'Produto'}</td>
                  <td>{produto.quantidade || '0'}</td>
                  <td>{formatCurrency(produto.valorUnitario)}</td>
                  <td>{formatCurrency(produto.valorTotal)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderClassification = () => {
    const data = getData();
    if (!data) return null;

    console.log('üîç Buscando classifica√ß√£o em:', data);
    
    // Tentar encontrar classifica√ß√£o em v√°rios lugares
    const classificacao = data.classificacaoDespesa || data.categoria || data.categorias || data.tipoDespesa;
    
    console.log('üè∑Ô∏è Classifica√ß√£o encontrada:', classificacao);

    let displayValue = 'N√£o classificada';
    
    if (Array.isArray(classificacao) && classificacao.length > 0) {
      displayValue = classificacao.join(', ');
    } else if (classificacao && typeof classificacao === 'string') {
      displayValue = classificacao;
    }

    return (
      <div className="classification-section">
        <h3>Classifica√ß√£o da Despesa</h3>
        <div className="classification-info">
          <p><strong>Categoria:</strong> <span className="info-value">{displayValue}</span></p>
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
  };

  const handleSelectHistoryNote = (noteData) => {
    console.log('üìã Nota selecionada do hist√≥rico:', noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: { fromHistory: true }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Fa√ßa upload de uma nota fiscal em PDF</p>
      
      <div className="history-button-container">
        <button type="button" onClick={handleShowHistory} className="history-button">
          üìã Ver Hist√≥rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button type="submit" disabled={loading || !file} className="process-button">
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {result && (
        <div className="result-container">
          <h2>Dados Extra√≠dos da Nota Fiscal</h2>
          
          {/* Informa√ß√µes B√°sicas */}
          <div className="summary-section">
            <h3>Informa√ß√µes da Nota</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">N√∫mero NF:</span>
                <span className="summary-value">{getData()?.numeroNotaFiscal || getData()?.numero || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data Emiss√£o:</span>
                <span className="summary-value">{getData()?.dataEmissao || getData()?.data || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(getData()?.valorTotal || getData()?.total)}</span>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor</h3>
            <div className="supplier-info">
              <p><strong>Raz√£o Social:</strong> <span className="info-value">
                {getData()?.fornecedor?.razaoSocial || getData()?.emitente?.razaoSocial || 'N/A'}
              </span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">
                {formatCNPJ(getData()?.fornecedor?.cnpj || getData()?.emitente?.cnpj)}
              </span></p>
            </div>
          </div>

          {/* Cliente */}
          <div className="client-section">
            <h3>Cliente</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">
                {getData()?.cliente?.nome || getData()?.destinatario?.nome || 'N/A'}
              </span></p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts()}

          {/* Classifica√ß√£o */}
          {renderClassification()}

          {/* Debug */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
