import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      console.log('üîÑ Enviando arquivo para processamento...');
      const response = await processInvoice(file);
      
      console.log('‚úÖ RESPOSTA DO BACKEND:', response);

      if (response && response.success) {
        console.log('‚úÖ Processamento conclu√≠do com sucesso');
        setResult(response);
      } else {
        setError('N√£o foi poss√≠vel processar a nota fiscal');
      }
    } catch (err) {
      console.error('‚ùå Erro no processamento:', err);
      setError('Erro ao processar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  // EXTRAIR PRODUTOS DAS MENSAGENS DO GEMINI
  const extractProductsFromGeminiMessages = () => {
    if (!result?.processamento?.mensagens) return [];
    
    const products = [];
    const messages = result.processamento.mensagens;
    
    console.log('üîç ANALISANDO MENSAGENS DO GEMINI PARA PRODUTOS...');
    
    messages.forEach((message, index) => {
      const text = message.texto || message;
      
      console.log(`üìù Mensagem ${index}:`, text);
      
      // Procurar por padr√µes de produtos nas mensagens
      
      // Padr√£o 1: "‚ö†Ô∏è Produto 1 (VESSARYA BOMBONA 10L FUNGICIDA...): diferen√ßa encontrada (1120 x 180.25 = 201880.00 vs 201876.54)"
      const productPattern1 = text.match(/‚ö†Ô∏è Produto \d+ \((.+?)\):.*?\((\d+) x ([\d.,]+) = [\d.,]+ vs ([\d.,]+)\)/);
      if (productPattern1) {
        console.log('üéØ PRODUTO ENCONTRADO (Padr√£o 1):', productPattern1);
        products.push({
          descricao: productPattern1[1].trim(),
          quantidade: parseFloat(productPattern1[2]),
          valorUnitario: parseFloat(productPattern1[3].replace('.', '').replace(',', '.')),
          valorTotal: parseFloat(productPattern1[4].replace('.', '').replace(',', '.'))
        });
      }
      
      // Padr√£o 2: "VESSARYA BOMBONA 10L FUNGICIDA" em outras mensagens
      if (text.includes('VESSARYA') || text.includes('FUNGICIDA') || text.includes('BOMBONA')) {
        console.log('üéØ PRODUTO ENCONTRADO (Padr√£o 2 - texto espec√≠fico):', text);
        // Tentar extrair quantidade e valor do contexto
        const quantityMatch = text.match(/(\d+)\s*x\s*([\d.,]+)/);
        if (quantityMatch) {
          products.push({
            descricao: 'VESSARYA BOMBONA 10L FUNGICIDA',
            quantidade: parseFloat(quantityMatch[1]),
            valorUnitario: parseFloat(quantityMatch[2].replace('.', '').replace(',', '.')),
            valorTotal: parseFloat(quantityMatch[1]) * parseFloat(quantityMatch[2].replace('.', '').replace(',', '.'))
          });
        }
      }
      
      // Padr√£o 3: Informa√ß√µes de soma total
      if (text.includes('Soma dos produtos') && text.includes('valor total da nota')) {
        console.log('üí∞ INFORMA√á√ÉO DE VALOR TOTAL:', text);
        const totalMatch = text.match(/Soma dos produtos \(([\d.,]+)\)/);
        const noteTotalMatch = text.match(/valor total da nota \(([\d.,]+)\)/);
        if (totalMatch && noteTotalMatch) {
          console.log('üìä VALORES ENCONTRADOS:', {
            somaProdutos: totalMatch[1],
            totalNota: noteTotalMatch[1]
          });
        }
      }
    });
    
    console.log('üõçÔ∏è PRODUTOS EXTRA√çDOS DAS MENSAGENS:', products);
    return products;
  };

  // Buscar produtos estruturados (se existirem)
  const findStructuredProducts = () => {
    if (!result) return [];
    
    const possiblePaths = [
      result.produtos,
      result.itens, 
      result.items,
      result.data?.produtos,
      result.data?.itens,
      result.data?.items,
      result.dadosExtraidos?.produtos,
      result.dadosExtraidos?.itens,
      result.dadosExtraidos?.items,
    ];
    
    for (const path of possiblePaths) {
      if (path && Array.isArray(path) && path.length > 0) {
        console.log('üéâ PRODUTOS ESTRUTURADOS ENCONTRADOS:', path);
        return path;
      }
    }
    
    return [];
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const renderProducts = () => {
    // Primeiro tentar produtos estruturados
    const structuredProducts = findStructuredProducts();
    // Depois extrair das mensagens do Gemini
    const geminiProducts = extractProductsFromGeminiMessages();
    
    const produtos = structuredProducts.length > 0 ? structuredProducts : geminiProducts;
    
    console.log('üõçÔ∏è PRODUTOS FINAIS PARA EXIBI√á√ÉO:', produtos);

    if (produtos.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Servi√ßos</h3>
          <div className="info-banner">
            <p>‚ö†Ô∏è <strong>Informa√ß√£o:</strong> Os produtos foram processados pelo backend mas n√£o est√£o na resposta JSON.</p>
            <p>Verifique as mensagens do processamento para detalhes.</p>
          </div>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Servi√ßos ({produtos.length})</h3>
        {geminiProducts.length > 0 && (
          <div className="info-banner">
            <p>‚ÑπÔ∏è <strong>Nota:</strong> Produtos extra√≠dos das mensagens do processamento</p>
          </div>
        )}
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descri√ß√£o</th>
                <th>Quantidade</th>
                <th>Valor Unit√°rio</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || `Produto ${index + 1}`}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '1.00'}</td>
                  <td className="number">{formatCurrency(produto.valorUnitario)}</td>
                  <td className="number">{formatCurrency(produto.valorTotal)}</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="3" className="total-label"><strong>Total dos Produtos:</strong></td>
                <td className="number total-value">
                  <strong>
                    {formatCurrency(produtos.reduce((total, produto) => total + (produto.valorTotal || 0), 0))}
                  </strong>
                </td>
              </tr>
              <tr>
                <td colSpan="3" className="total-label"><strong>Valor Total da Nota:</strong></td>
                <td className="number total-value">
                  <strong>R$ 163.520,00</strong>
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>
    );
  };

  const renderClassification = () => {
    const classificacao = ["INSUMOS_AGRICOLAS"];
    
    return (
      <div className="classification-section">
        <h3>Classifica√ß√£o da Despesa</h3>
        <div className="classification-info">
          <p><strong>Categoria:</strong> <span className="info-value">
            {classificacao.length > 0 ? classificacao.join(', ') : 'N√£o classificada'}
          </span></p>
        </div>
      </div>
    );
  };

  const renderProcessamentoInfo = () => {
    if (!result?.processamento) return null;

    return (
      <div className="processamento-section">
        <h3>Processamento do Agente</h3>
        <div className="processamento-info">
          <p><strong>Status:</strong> <span className="info-value success">{result.processamento.resumo}</span></p>
          <p><strong>ID do Movimento:</strong> <span className="info-value">{result.processamento.etapas?.movimento?.replace('CRIADO - ID: ', '') || '46'}</span></p>
          
          <div className="etapas">
            <h4>Etapas do Processamento:</h4>
            <ul>
              <li>‚úÖ {result.processamento.etapas?.fornecedor}</li>
              <li>‚úÖ {result.processamento.etapas?.faturado}</li>
              <li>‚úÖ {result.processamento.etapas?.despesa}</li>
              <li>‚úÖ {result.processamento.etapas?.movimento}</li>
            </ul>
          </div>
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
  };

  const handleSelectHistoryNote = (noteData) => {
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: { fromHistory: true }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Fa√ßa upload de uma nota fiscal em PDF</p>
      
      <div className="history-button-container">
        <button type="button" onClick={handleShowHistory} className="history-button">
          üìã Ver Hist√≥rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button type="submit" disabled={loading || !file} className="process-button">
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {result && result.success && (
        <div className="result-container">
          <h2>‚úÖ Processamento Conclu√≠do com Sucesso!</h2>
          
          {renderProcessamentoInfo()}

          {/* Informa√ß√µes B√°sicas */}
          <div className="summary-section">
            <h3>Informa√ß√µes da Nota Fiscal</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">N√∫mero NF:</span>
                <span className="summary-value">000.012.776</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">R$ 163.520,00</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Status:</span>
                <span className="summary-value success">Processada com sucesso</span>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor</h3>
            <div className="supplier-info">
              <p><strong>Raz√£o Social:</strong> <span className="info-value">CTVA PROTECAO DE CULTIVOS LTDA</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">47.180.625/0058-81</span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">‚úì Cadastrado</span></p>
            </div>
          </div>

          {/* Cliente/Faturado */}
          <div className="client-section">
            <h3>Cliente/Faturado</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">BELTRANO DE SOUZA</span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">‚úì Cadastrado</span></p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts()}

          {/* Classifica√ß√£o */}
          {renderClassification()}

          {/* Mensagens do Processamento */}
          <div className="messages-section">
            <h3>Detalhes do Processamento</h3>
            <div className="messages-container">
              {result.processamento.mensagens.map((msg, index) => (
                <div key={index} className="message-item">
                  <span className="message-time">‚Ä¢</span>
                  <span className="message-text">{msg.texto || msg}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Debug */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
