import React, { useEffect, useState } from 'react';
import DataTable from '../components/DataTable';
import { listClassificacao, createClassificacao, updateClassificacao, deleteClassificacao } from '../services/api';

export default function ClassificationPage() {
  const [rows, setRows] = useState([]);
  const [q, setQ] = useState('');
  const [tipo, setTipo] = useState('');
  const [status, setStatus] = useState('ATIVO');
  const [sortBy, setSortBy] = useState('descricao');
  const [sortOrder, setSortOrder] = useState('asc');

  const columns = [
    { key: 'descricao', label: 'Descrição' },
    { key: 'tipo', label: 'Tipo' }
  ];

  const load = async () => {
    const params = { q, tipo, status, sort: sortBy, order: sortOrder };
    const data = await listClassificacao(params);
    setRows(data?.data || []);
  };

  // Inicia vazia; só carrega via Buscar/Todos
  const onSort = (key, order) => { setSortBy(key); setSortOrder(order); load(); };

  const onBuscar = () => load();
  const onTodos = () => { setStatus('ATIVO'); setQ(''); load(); };

  const onEdit = async (row) => {
    const descricao = prompt('Descrição', row.descricao);
    if (descricao === null) return;
    await updateClassificacao(row.id, { descricao });
    load();
  };

  const onDelete = async (row) => {
    if (!confirm(`Inativar ${row.descricao}?`)) return;
    await deleteClassificacao(row.id);
    load();
  };

  const onCreate = async () => {
    const descricao = prompt('Descrição');
    if (!descricao) return;
    const tipoVal = prompt('Tipo (RECEITA|DESPESA)', 'RECEITA');
    await createClassificacao({ tipo: tipoVal || 'RECEITA', descricao });
    load();
  };

  return (
    <div>
      <h2>Classificação</h2>
      <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
        <input placeholder="Buscar por descrição" value={q} onChange={(e)=>setQ(e.target.value)} style={{ flex: 1 }} />
        <select value={tipo} onChange={(e)=>setTipo(e.target.value)}>
          <option value="">Tipo</option>
          <option value="RECEITA">Receita</option>
          <option value="DESPESA">Despesa</option>
        </select>
        <button onClick={onBuscar}>Buscar</button>
        <button onClick={onTodos}>Todos (ATIVO)</button>
        <button onClick={onCreate}>+ Inserir Novo</button>
      </div>
      <DataTable columns={columns} rows={rows} onSort={onSort} sortBy={sortBy} sortOrder={sortOrder} onEdit={onEdit} onDelete={onDelete} />
    </div>
  );
}
