import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    if (file.type !== 'application/pdf') {
      setError('Por favor, selecione apenas arquivos PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await processInvoice(file);
      setResult(data);
    } catch (err) {
      setError('Erro ao processar o arquivo. Verifique se o backend estÃ¡ rodando.');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderProducts = (products) => {
    if (!products || products.length === 0) return null;

    return (
      <div className="products-section">
        <h3>Produtos/ServiÃ§os ({products.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>CÃ³digo</th>
                <th>DescriÃ§Ã£o</th>
                <th>Quantidade</th>
                <th>UnitÃ¡rio</th>
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.codigo || '-'}</td>
                  <td>{produto.descricao || '-'}</td>
                  <td>{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td>{produto.valorUnitario ? produto.valorUnitario.toFixed(2) : '0.00'}</td>
                  <td>{produto.valorTotal ? produto.valorTotal.toFixed(2) : '0.00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (parcelas) => {
    if (!parcelas || parcelas.length === 0) return null;

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
                <th>Forma Pagamento</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{parcela.numero || index + 1}</td>
                  <td>{parcela.dataVencimento || '-'}</td>
                  <td>{parcela.valor ? parcela.valor.toFixed(2) : '0.00'}</td>
                  <td>{parcela.formaPagamento || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value || 0);
  };

  return (
    <div className="invoice-processor">
      <h1>Processador de Notas Fiscais</h1>
      <p>FaÃ§a upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {result && result.data && (
        <div className="result-container">
          <h2>Dados ExtraÃ­dos da Nota Fiscal</h2>
          
          {/* InformaÃ§Ãµes BÃ¡sicas */}
          <div className="summary-section">
            <h3>Resumo</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">NÃºmero NF:</span>
                <span className="summary-value">{result.data.notaFiscal?.numero || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data EmissÃ£o:</span>
                <span className="summary-value">{result.data.notaFiscal?.dataEmissao || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(result.data.notaFiscal?.valorTotal)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Fornecedor:</span>
                <span className="summary-value">{result.data.fornecedor?.razaoSocial || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">CNPJ:</span>
                <span className="summary-value">{result.data.fornecedor?.cnpj || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">ClassificaÃ§Ã£o:</span>
                <span className="summary-value">
                  {result.data.informacoesAdicionais?.classificacaoDespesa?.join(', ') || 'N/A'}
                </span>
              </div>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts(result.data.produtos)}

          {/* Parcelas */}
          {renderParcelas(result.data.parcelas)}

          {/* JSON Completo (para debug) */}
          <details className="json-details">
            <summary>JSON Completo</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}
    </div>
  );
};

export default InvoiceProcessor;
