import React, { useEffect, useState } from 'react';
import DataTable from '../components/DataTable';
import { listPessoas, createPessoa, updatePessoa, deletePessoa } from '../services/api';

export default function PeoplePage() {
  const [rows, setRows] = useState([]);
  const [q, setQ] = useState('');
  const [tipo, setTipo] = useState('');
  const [status, setStatus] = useState('ATIVO');
  const [sortBy, setSortBy] = useState('razao_social');
  const [sortOrder, setSortOrder] = useState('asc');

  const columns = [
    { key: 'razao_social', label: 'Razão Social' },
    { key: 'nome_fantasia', label: 'Fantasia' },
    { key: 'cnpj_cpf', label: 'CNPJ/CPF' },
    { key: 'tipo', label: 'Tipo' }
  ];

  const load = async () => {
    const params = { q, tipo, status, sort: sortBy, order: sortOrder };
    const data = await listPessoas(params);
    setRows(data?.data || []);
  };

  // Inicia vazia; só carrega via Buscar/Todos

  const onSort = (key, order) => { setSortBy(key); setSortOrder(order); load(); };

  const onBuscar = () => load();
  const onTodos = () => { setStatus('ATIVO'); setQ(''); load(); };

  const onEdit = async (row) => {
    const razaoSocial = prompt('Razão Social', row.razao_social);
    const nomeFantasia = prompt('Nome Fantasia', row.nome_fantasia || '');
    if (razaoSocial === null) return;
    await updatePessoa(row.id, { razaoSocial, nomeFantasia });
    load();
  };

  const onDelete = async (row) => {
    if (!confirm(`Inativar ${row.razao_social}?`)) return;
    await deletePessoa(row.id);
    load();
  };

  const onCreate = async () => {
    const razaoSocial = prompt('Razão Social');
    if (!razaoSocial) return;
    const nomeFantasia = prompt('Nome Fantasia');
    const cnpjCpf = prompt('CNPJ/CPF');
    const tipoVal = prompt('Tipo (FORNECEDOR|CLIENTE|FATURADO)', 'FORNECEDOR');
    await createPessoa({ tipo: tipoVal || 'FORNECEDOR', razaoSocial, nomeFantasia, cnpjCpf });
    load();
  };

  return (
    <div>
      <h2>Pessoas</h2>
      <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
        <input placeholder="Buscar por nome, fantasia ou documento" value={q} onChange={(e)=>setQ(e.target.value)} style={{ flex: 1 }} />
        <select value={tipo} onChange={(e)=>setTipo(e.target.value)}>
          <option value="">Tipo</option>
          <option value="FORNECEDOR">Fornecedor</option>
          <option value="CLIENTE">Cliente</option>
          <option value="FATURADO">Faturado</option>
        </select>
        <button onClick={onBuscar}>Buscar</button>
        <button onClick={onTodos}>Todos (ATIVO)</button>
        <button onClick={onCreate}>+ Inserir Novo</button>
      </div>
      <DataTable columns={columns} rows={rows} onSort={onSort} sortBy={sortBy} sortOrder={sortOrder} onEdit={onEdit} onDelete={onDelete} />
    </div>
  );
}
