import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    if (file.type !== 'application/pdf') {
      setError('Por favor, selecione apenas arquivos PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const data = await processInvoice(file);
      setResult(data);
    } catch (err) {
      setError('Erro ao processar o arquivo. Verifique se o backend está rodando.');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderProducts = (products) => {
    if (!products || products.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Serviços</h3>
          <p>Nenhum produto encontrado na nota fiscal</p>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Serviços ({products.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Quantidade</th>
                <th>Valor Unitário</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || 'Produto não especificado'}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                  <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (parcelas) => {
    if (!parcelas || parcelas.length === 0) return null;

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
                <th>Forma Pagamento</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{parcela.numero || index + 1}</td>
                  <td>{parcela.dataVencimento || '-'}</td>
                  <td className="number">{parcela.valor ? formatCurrency(parcela.valor) : 'R$ 0,00'}</td>
                  <td>{parcela.formaPagamento || 'Não especificado'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value || 0);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj) return 'N/A';
    return cnpj.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  return (
    <div className="invoice-processor">
      <h1>Processador de Notas Fiscais</h1>
      <p>Faça upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {result && result.data && (
        <div className="result-container">
          <h2>Dados Extraídos da Nota Fiscal</h2>
          
          {/* Informações Básicas */}
          <div className="summary-section">
            <h3>Informações da Nota</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">Número NF:</span>
                <span className="summary-value">{result.data.notaFiscal?.numero || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Série:</span>
                <span className="summary-value">{result.data.notaFiscal?.serie || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data Emissão:</span>
                <span className="summary-value">{result.data.notaFiscal?.dataEmissao || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(result.data.notaFiscal?.valorTotal)}</span>
              </div>
            </div>
          </div>

          {/* Fornecedor (Emitente) */}
          <div className="supplier-section">
            <h3>Fornecedor (Emitente)</h3>
            <div className="supplier-info">
              <p><strong>Razão Social:</strong> {result.data.fornecedor?.razaoSocial || 'N/A'}</p>
              <p><strong>Nome Fantasia:</strong> {result.data.fornecedor?.fantasia || 'N/A'}</p>
              <p><strong>CNPJ:</strong> {formatCNPJ(result.data.fornecedor?.cnpj) || 'N/A'}</p>
              <p><strong>Inscrição Estadual:</strong> {result.data.fornecedor?.inscricaoEstadual || 'N/A'}</p>
              {result.data.fornecedor?.endereco && (
                <p><strong>Endereço:</strong> {`
                  ${result.data.fornecedor.endereco.logradouro || ''} 
                  ${result.data.fornecedor.endereco.numero || ''}, 
                  ${result.data.fornecedor.endereco.bairro || ''} - 
                  ${result.data.fornecedor.endereco.cidade || ''}/ 
                  ${result.data.fornecedor.endereco.estado || ''}
                `.trim()}</p>
              )}
            </div>
          </div>

          {/* Cliente (Destinatário) */}
          {result.data.cliente?.nome && (
            <div className="client-section">
              <h3>Cliente (Destinatário)</h3>
              <div className="client-info">
                <p><strong>Nome:</strong> {result.data.cliente.nome}</p>
                <p><strong>CPF/CNPJ:</strong> {result.data.cliente.cpfCnpj || 'N/A'}</p>
                <p><strong>Inscrição Estadual:</strong> {result.data.cliente.inscricaoEstadual || 'N/A'}</p>
              </div>
            </div>
          )}

          {/* Classificação */}
          <div className="classification-section">
            <h3>Classificação da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> {result.data.informacoesAdicionais?.classificacaoDespesa?.join(', ') || 'Não classificada'}</p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts(result.data.produtos)}

          {/* Parcelas */}
          {renderParcelas(result.data.parcelas)}

          {/* JSON Completo (para debug) */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}
    </div>
  );
};

export default InvoiceProcessor;
