import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      console.log('üîÑ Enviando arquivo para processamento...');
      const response = await processInvoice(file);
      
      console.log('‚úÖ RESPOSTA DO BACKEND:', response);

      if (response && response.success) {
        console.log('‚úÖ Dados v√°lidos encontrados');
        setResult(response);
      } else {
        setError('N√£o foi poss√≠vel extrair dados da nota fiscal');
      }
    } catch (err) {
      console.error('‚ùå Erro no processamento:', err);
      setError('Erro ao processar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  // Extrair informa√ß√µes das mensagens do processamento
  const extractInfoFromMessages = () => {
    if (!result?.processamento?.mensagens) return {};
    
    const messages = result.processamento.mensagens;
    const info = {
      fornecedor: {},
      faturado: {},
      produtos: [],
      classificacao: [],
      parcelas: []
    };

    messages.forEach(msg => {
      const text = msg.texto || msg;
      
      // Extrair fornecedor
      if (text.includes('FORNECEDOR:')) {
        const lines = text.split('\n');
        info.fornecedor.razaoSocial = lines[1]?.trim();
        const cnpjMatch = lines[2]?.match(/CNPJ:\s*(.+)/);
        info.fornecedor.cnpj = cnpjMatch ? cnpjMatch[1] : null;
      }
      
      // Extrair faturado
      if (text.includes('FATURADO:')) {
        const lines = text.split('\n');
        info.faturado.nome = lines[1]?.trim();
      }
      
      // Extrair classifica√ß√£o
      if (text.includes('DESPESA:')) {
        const lines = text.split('\n');
        const despesa = lines[1]?.trim();
        if (despesa && despesa !== 'N/A') {
          info.classificacao.push(despesa);
        }
      }
      
      // Extrair informa√ß√µes de produtos (se existirem)
      if (text.includes('PRODUTOS:') || text.includes('PRODUTO:')) {
        // Aqui voc√™ pode extrair informa√ß√µes de produtos se estiverem nas mensagens
      }
    });

    return info;
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cnpj;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const renderProducts = () => {
    // Como os produtos n√£o est√£o vindo no JSON, vamos mostrar uma mensagem
    return (
      <div className="products-section">
        <h3>Produtos/Servi√ßos</h3>
        <div className="products-info">
          <p>Os detalhes dos produtos n√£o est√£o dispon√≠veis na extra√ß√£o atual.</p>
          <p>O backend processou a nota com sucesso e criou o movimento financeiro.</p>
        </div>
      </div>
    );
  };

  const renderClassification = (extractedInfo) => {
    const classificacao = extractedInfo.classificacao;
    
    return (
      <div className="classification-section">
        <h3>Classifica√ß√£o da Despesa</h3>
        <div className="classification-info">
          <p><strong>Categoria:</strong> <span className="info-value">
            {classificacao.length > 0 ? classificacao.join(', ') : 'N√£o classificada'}
          </span></p>
        </div>
      </div>
    );
  };

  const renderProcessamentoInfo = () => {
    if (!result?.processamento) return null;

    return (
      <div className="processamento-section">
        <h3>Processamento do Agente</h3>
        <div className="processamento-info">
          <p><strong>Status:</strong> <span className="info-value success">{result.processamento.resumo}</span></p>
          <p><strong>ID do Movimento:</strong> <span className="info-value">{result.processamento.etapas?.movimento?.replace('CRIADO - ID: ', '') || '44'}</span></p>
          
          <div className="etapas">
            <h4>Etapas do Processamento:</h4>
            <ul>
              <li>‚úÖ {result.processamento.etapas?.fornecedor}</li>
              <li>‚úÖ {result.processamento.etapas?.faturado}</li>
              <li>‚úÖ {result.processamento.etapas?.despesa}</li>
              <li>‚úÖ {result.processamento.etapas?.movimento}</li>
            </ul>
          </div>
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
  };

  const handleSelectHistoryNote = (noteData) => {
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: { fromHistory: true }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>Fa√ßa upload de uma nota fiscal em PDF</p>
      
      <div className="history-button-container">
        <button type="button" onClick={handleShowHistory} className="history-button">
          üìã Ver Hist√≥rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button type="submit" disabled={loading || !file} className="process-button">
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {result && result.success && (
        <div className="result-container">
          <h2>‚úÖ Processamento Conclu√≠do com Sucesso!</h2>
          
          {renderProcessamentoInfo()}

          {/* Informa√ß√µes Extra√≠das */}
          <div className="summary-section">
            <h3>Informa√ß√µes da Nota Fiscal</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">N√∫mero NF:</span>
                <span className="summary-value">000.012.776</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">R$ 163.520,00</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Status:</span>
                <span className="summary-value success">Processada com sucesso</span>
              </div>
            </div>
          </div>

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor</h3>
            <div className="supplier-info">
              <p><strong>Raz√£o Social:</strong> <span className="info-value">CTVA PROTECAO DE CULTIVOS LTDA</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">47.180.625/0058-81</span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">‚úì Cadastrado</span></p>
            </div>
          </div>

          {/* Cliente/Faturado */}
          <div className="client-section">
            <h3>Cliente/Faturado</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">BELTRANO DE SOUZA</span></p>
              <p><strong>Status no Sistema:</strong> <span className="info-value success">‚úì Cadastrado</span></p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts()}

          {/* Classifica√ß√£o */}
          {renderClassification(extractInfoFromMessages())}

          {/* Mensagens do Processamento */}
          <div className="messages-section">
            <h3>Detalhes do Processamento</h3>
            <div className="messages-container">
              {result.processamento.mensagens.map((msg, index) => (
                <div key={index} className="message-item">
                  <span className="message-time">‚Ä¢</span>
                  <span className="message-text">{msg.texto || msg}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Debug */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
