import React, { useState } from 'react';
// REMOVA qualquer importação de CSS por enquanto

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => setLoading(false), 2000);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Processador de Notas Fiscais</h1>
      <p>Faça upload de uma nota fiscal em PDF</p>
      
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept=".pdf"
          onChange={handleFileChange}
          style={{ margin: '10px 0', padding: '10px' }}
        />
        <br />
        <button 
          type="submit" 
          disabled={loading}
          style={{
            padding: '10px 20px',
            background: loading ? '#ccc' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {file && <p>Arquivo selecionado: {file.name}</p>}
    </div>
  );
};

// VERIFIQUE se a exportação está correta
export default InvoiceProcessor;
