import React, { useState, useEffect } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  
  // Estado para mensagens do agente inteligente
  const [agentMessages, setAgentMessages] = useState([]);
  const [agentProcessing, setAgentProcessing] = useState(false);

  // DEBUG: Monitorar mudan√ßas no result
  useEffect(() => {
    console.log('=== STATE RESULT ATUALIZADO ===');
    console.log('Result:', result);
    console.log('Result?.data:', result?.data);
    console.log('Result?.success:', result?.success);
    console.log('=== FIM DEBUG ===');
  }, [result]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  // Fun√ß√£o para adicionar mensagens do agente
  const addAgentMessage = (message) => {
    setAgentMessages(prev => [...prev, {
      id: Date.now() + Math.random().toString(36).substr(2, 9),
      texto: message,
      timestamp: new Date()
    }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    setLoading(true);
    setAgentProcessing(true);
    setError('');
    setResult(null);
    setAgentMessages([]);

    try {
      addAgentMessage('üöÄ Iniciando processamento da nota fiscal...');
      
      console.log('üì§ Enviando arquivo para backend...');
      const response = await processInvoice(file);
      
      console.log('=== DEBUG FRONTEND - RESPONSE ===');
      console.log('Response completo:', response);
      console.log('Response.success:', response?.success);
      console.log('Response.data:', response?.data);
      console.log('Response.dadosNota:', response?.dadosNota);
      console.log('=== FIM DEBUG ===');

      // Processar mensagens do agente
      if (response.processamento && response.processamento.mensagens) {
        console.log('üì® Mensagens do agente:', response.processamento.mensagens);
        setAgentMessages(response.processamento.mensagens);
      }

      if (response.success) {
        // CORRE√á√ÉO RADICAL: SEMPRE definir o resultado, ignorar valida√ß√£o
        console.log('‚úÖ CORRE√á√ÉO RADICAL: Definindo resultado SEM valida√ß√£o');
        setResult(response);
        addAgentMessage('‚úÖ Processamento conclu√≠do com sucesso!');
        
        // Debug adicional para verificar se os dados est√£o sendo definidos
        setTimeout(() => {
          console.log('=== VERIFICA√á√ÉO DO STATE RESULT ===');
          console.log('Result ap√≥s setResult:', result);
        }, 100);
        
      } else {
        setError(response?.error || 'Erro desconhecido no processamento');
        addAgentMessage('‚ùå Erro no processamento');
      }
      
    } catch (err) {
      console.error('üí• Erro no frontend:', err);
      setError('Erro ao processar o arquivo');
      addAgentMessage('‚ùå Erro de comunica√ß√£o com o servidor');
    } finally {
      setLoading(false);
      setAgentProcessing(false);
    }
  };

  // Componente para exibir mensagens do agente - VERS√ÉO CORRIGIDA
  const AgentMessagesPanel = () => {
    if (agentMessages.length === 0 && !agentProcessing) {
      return null;
    }

    return (
      <div className="agent-messages-panel">
        <h3>ü§ñ Agente Inteligente - Processamento</h3>
        <div className="agent-messages-container">
          {agentMessages.map((message, index) => {
            // CORRE√á√ÉO: Usar index como fallback se id for duplicado
            const uniqueKey = message.id ? `${message.id}_${index}` : `msg_${index}`;
            const messageText = message?.texto || '';
            const isError = messageText.includes('‚ùå') || messageText.toLowerCase().includes('erro');
            const isSuccess = messageText.includes('‚úÖ') || messageText.toLowerCase().includes('sucesso');
            
            return (
              <div 
                key={uniqueKey}
                className={`agent-message ${isError ? 'error' : ''} ${isSuccess ? 'success' : ''}`}
              >
                <span className="agent-message-time">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString() : new Date().toLocaleTimeString()}
                </span>
                <div className="agent-message-text">
                  {messageText.split('\n').map((line, lineIndex) => (
                    <div key={`${uniqueKey}_line_${lineIndex}`}>{line}</div>
                  ))}
                </div>
              </div>
            );
          })}
          
          {agentProcessing && agentMessages.length === 0 && (
            <div className="agent-message" key="processing_initial">
              <span className="agent-message-time">{new Date().toLocaleTimeString()}</span>
              <div className="agent-message-text">‚è≥ Iniciando processamento...</div>
            </div>
          )}
          
          {agentProcessing && (
            <div className="agent-processing-indicator" key="processing_indicator">
              <div className="agent-spinner"></div>
              <span>Processando com Agente Inteligente...</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  // Fun√ß√µes de formata√ß√£o
  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value || 0);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A' || cnpj === '') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A' || cpf === '') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatEndereco = (endereco) => {
    if (!endereco || endereco === 'N/A' || endereco === '') return 'N/A';
    return endereco;
  };

  const renderDocumentoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo === 'PF' && cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') {
      return formatCPF(cliente.cpf);
    } else if (cliente.tipo === 'PJ' && cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') {
      return formatCNPJ(cliente.cnpj);
    } else if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return formatCPF(doc);
      if (doc.length === 14) return formatCNPJ(doc);
      return cliente.documento;
    }
    
    return 'N/A';
  };

  const renderProducts = (products) => {
    if (!products || products.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/Servi√ßos</h3>
          <p>Nenhum produto encontrado na nota fiscal</p>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/Servi√ßos ({products.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>Descri√ß√£o</th>
                <th>Quantidade</th>
                <th>Valor Unit√°rio</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || 'Produto n√£o especificado'}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                  <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (parcelas) => {
    if (!parcelas || parcelas.length === 0) return null;

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{parcela.numeroParcela || index + 1}</td>
                  <td>{parcela.dataVencimento || 'N/A'}</td>
                  <td className="number">{formatCurrency(parcela.valor)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const getTipoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo) return cliente.tipo;
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') return 'PF';
    if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') return 'PJ';
    if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return 'PF';
      if (doc.length === 14) return 'PJ';
    }
    
    return 'N/A';
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      {/* DEBUG VISUAL */}
      <div style={{background: '#ffeb3b', padding: '10px', margin: '10px 0', border: '2px solid red'}}>
        <strong>DEBUG:</strong> 
        Result: {result ? 'EXISTE' : 'NULO'}, 
        Result.data: {result?.data ? 'EXISTE' : 'NULO'},
        Result.success: {result?.success ? 'SIM' : 'N√ÉO'}
      </div>

      <h1>LSR - NF</h1>
      <p>Fa√ßa upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      {/* Bot√£o do Hist√≥rico */}
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          üìã Ver Hist√≥rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      {/* Painel de Mensagens do Agente Inteligente */}
      <AgentMessagesPanel />

      {error && (
        <div className={`error-message ${error.includes('NFE Inv√°lida') ? 'nfe-invalida' : ''}`}>
          {error}
        </div>
      )}

      {/* CORRE√á√ÉO: Removida a valida√ß√£o isDataEmpty */}
      {result && result.data && (
        <div className="result-container">
          <h2>
            Dados Extra√≠dos da Nota Fiscal
            {result.metadata?.fromHistory && <span className="history-indicator">DO HIST√ìRICO</span>}
            {result.processamento?.processadoPor && (
              <span className="agent-indicator">ü§ñ PROCESSADO POR AGENTE INTELIGENTE</span>
            )}
          </h2>
          
          {/* Informa√ß√µes B√°sicas */}
          <div className={`summary-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>Informa√ß√µes da Nota</h3>
            <div className="summary-grid">
              <div className="summary-item">
                <span className="summary-label">N√∫mero NF:</span>
                <span className="summary-value">{result.data.numeroNotaFiscal || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Data Emiss√£o:</span>
                <span className="summary-value">{result.data.dataEmissao || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Total:</span>
                <span className="summary-value">{formatCurrency(result.data.valorTotal)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Valor Frete:</span>
                <span className="summary-value">{formatCurrency(result.data.valorFrete || 0)}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Parcelas:</span>
                <span className="summary-value">{result.data.quantidadeParcelas || 0}</span>
              </div>
              {result.processamento?.idMovimento && (
                <div className="summary-item">
                  <span className="summary-label">ID Movimento:</span>
                  <span className="summary-value agent-id">{result.processamento.idMovimento}</span>
                </div>
              )}
            </div>
          </div>

          {/* Fornecedor */}
          <div className={`supplier-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>Fornecedor (Emitente)</h3>
            <div className="supplier-info">
              <p><strong>Raz√£o Social:</strong> <span className="info-value">{result.data.fornecedor?.razaoSocial || 'N/A'}</span></p>
              <p><strong>Nome Fantasia:</strong> <span className="info-value">{result.data.fornecedor?.fantasia || 'N/A'}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(result.data.fornecedor?.cnpj) || 'N/A'}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{result.data.fornecedor?.tipo || 'PJ'}</span></p>
              <p><strong>Endere√ßo:</strong> <span className="address-line">{formatEndereco(result.data.fornecedor?.endereco)}</span></p>
            </div>
          </div>

          {/* Cliente */}
          <div className={`client-section ${result.metadata?.fromHistory ? 'result-from-history' : ''}`}>
            <h3>Cliente (Destinat√°rio)</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">{result.data.cliente?.nome || result.data.faturado?.nomeCompleto || 'N/A'}</span></p>
              <p><strong>Documento:</strong> <span className="info-value">{renderDocumentoCliente(result.data.cliente || result.data.faturado)}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{getTipoCliente(result.data.cliente || result.data.faturado)}</span></p>
              <p><strong>Endere√ßo:</strong> <span className="address-line">{formatEndereco(result.data.cliente?.endereco)}</span></p>
            </div>
          </div>

          {/* Produtos */}
          {renderProducts(result.data.produtos)}

          {/* Parcelas */}
          {renderParcelas(result.data.parcelas)}

          {/* Classifica√ß√£o */}
          <div className="classification-section">
            <h3>Classifica√ß√£o da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> <span className="info-value">{result.data.classificacaoDespesa?.join(', ') || 'N√£o classificada'}</span></p>
            </div>
          </div>

          {/* JSON Completo (para debug) */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      {/* Painel de Hist√≥rico */}
      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
