import React, { useMemo } from 'react';
import './DataTable.css';

export default function DataTable({ columns, rows, onSort, sortBy, sortOrder, onEdit, onDelete }) {
  const headers = useMemo(() => columns.map(c => ({ key: c.key, label: c.label })), [columns]);

  const handleSort = (key) => {
    const nextOrder = sortBy === key && sortOrder === 'asc' ? 'desc' : 'asc';
    onSort && onSort(key, nextOrder);
  };

  return (
    <div className="dt-container">
      <table className="dt-table">
        <thead>
          <tr>
            {headers.map(h => (
              <th key={h.key} onClick={() => handleSort(h.key)}>
                {h.label}
                {sortBy === h.key && (
                  <span className="dt-sort">{sortOrder === 'asc' ? ' ▲' : ' ▼'}</span>
                )}
              </th>
            ))}
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {rows.length === 0 ? (
            <tr>
              <td colSpan={headers.length + 1} className="dt-empty">Nenhum registro encontrado.</td>
            </tr>
          ) : (
            rows.map((r) => (
              <tr key={r.id}>
                {headers.map(h => (
                  <td key={h.key}>{r[h.key] ?? ''}</td>
                ))}
                <td className="dt-actions">
                  <button className="dt-btn" onClick={() => onEdit && onEdit(r)}>Editar</button>
                  <button className="dt-btn dt-danger" onClick={() => onDelete && onDelete(r)}>Excluir</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
