import React, { useState } from 'react';
import { processInvoice } from '../services/api';
import HistoryPanel from './HistoryPanel';
import './InvoiceProcessor.css';

const InvoiceProcessor = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);
  const [selectedHistoryNote, setSelectedHistoryNote] = useState(null);
  
  const [agentMessages, setAgentMessages] = useState([]);
  const [agentProcessing, setAgentProcessing] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setResult(null);
    setAgentMessages([]);
  };

  const addAgentMessage = (message) => {
    const newMessage = {
      id: Date.now() + Math.random(),
      texto: message || 'Mensagem sem conteÃºdo',
      timestamp: new Date()
    };
    setAgentMessages(prev => [...prev, newMessage]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setError('Por favor, selecione um arquivo PDF');
      return;
    }

    if (file.type !== 'application/pdf') {
      setError('Por favor, selecione apenas arquivos PDF');
      return;
    }

    setLoading(true);
    setAgentProcessing(true);
    setError('');
    setAgentMessages([]);
    
    try {
      addAgentMessage('ðŸš€ Iniciando processamento da nota fiscal...');
      addAgentMessage('ðŸ“„ Enviando arquivo para o servidor...');

      const response = await processInvoice(file);
      
      // DEBUG DETALHADO
      console.log('=== RESPOSTA COMPLETA DO BACKEND ===', response);
      console.log('Response.data:', response?.data);
      console.log('Response.dadosExtraidos:', response?.dadosExtraidos);
      
      // Processar mensagens do agente se existirem
      if (response.processamento && response.processamento.mensagens) {
        const safeMessages = response.processamento.mensagens.map(msg => ({
          id: msg.id || Date.now() + Math.random(),
          texto: msg.texto || 'Mensagem sem conteÃºdo',
          timestamp: msg.timestamp || new Date()
        }));
        setAgentMessages(safeMessages);
      }

      // CORREÃ‡ÃƒO CRÃTICA: Mapear os campos do backend para a estrutura esperada
      const extractedData = response?.data || response?.dadosExtraidos || response;
      
      console.log('=== DADOS EXTRAÃDOS PARA ANÃLISE ===', extractedData);
      
      // Mapear campos especÃ­ficos que podem ter nomes diferentes
      const normalizedData = {
        // InformaÃ§Ãµes bÃ¡sicas
        numeroNotaFiscal: extractedData.numeroNotaFiscal || extractedData.numero || extractedData.nfe,
        dataEmissao: extractedData.dataEmissao || extractedData.data,
        valorTotal: extractedData.valorTotal || extractedData.total,
        valorFrete: extractedData.valorFrete || extractedData.frete,
        chaveAcesso: extractedData.chaveAcesso || extractedData.chave,
        
        // Fornecedor/Emitente
        fornecedor: {
          razaoSocial: extractedData.fornecedor?.razaoSocial || extractedData.emitente?.razaoSocial || extractedData.fornecedor?.nome,
          fantasia: extractedData.fornecedor?.fantasia || extractedData.emitente?.fantasia,
          cnpj: extractedData.fornecedor?.cnpj || extractedData.emitente?.cnpj || extractedData.fornecedor?.documento,
          endereco: extractedData.fornecedor?.endereco || extractedData.emitente?.endereco
        },
        
        // Cliente/DestinatÃ¡rio
        cliente: {
          nome: extractedData.cliente?.nome || extractedData.destinatario?.nome || extractedData.faturado?.nome,
          documento: extractedData.cliente?.documento || extractedData.destinatario?.documento || extractedData.faturado?.documento,
          cpf: extractedData.cliente?.cpf || extractedData.destinatario?.cpf,
          cnpj: extractedData.cliente?.cnpj || extractedData.destinatario?.cnpj,
          endereco: extractedData.cliente?.endereco || extractedData.destinatario?.endereco
        },
        
        // Produtos - CORREÃ‡ÃƒO: Mapear diferentes nomes de campos
        produtos: extractedData.produtos || extractedData.itens || extractedData.items || [],
        
        // Parcelas
        parcelas: extractedData.parcelas || extractedData.duplicatas || [],
        
        // ClassificaÃ§Ã£o - CORREÃ‡ÃƒO: Buscar em diferentes locais
        classificacaoDespesa: extractedData.classificacaoDespesa || 
                             extractedData.categoria ||
                             extractedData.categorias ||
                             extractedData.tipoDespesa ||
                             []
      };

      console.log('=== DADOS NORMALIZADOS ===', normalizedData);
      console.log('Produtos encontrados:', normalizedData.produtos?.length || 0);
      console.log('ClassificaÃ§Ã£o:', normalizedData.classificacaoDespesa);

      if (!extractedData || isDataEmpty(normalizedData)) {
        console.log('Dados considerados vazios - mostrando erro');
        setError('NÃ£o foi possÃ­vel extrair dados suficientes da nota fiscal');
        setResult(null);
        addAgentMessage('âŒ Dados insuficientes extraÃ­dos da nota fiscal');
      } else {
        console.log('Dados vÃ¡lidos - mostrando resultado');
        setResult({
          ...response,
          data: normalizedData
        });
        
        if (response.processamento && response.processamento.idMovimento) {
          addAgentMessage(`ðŸŽ‰ Processamento concluÃ­do! Movimento ID: ${response.processamento.idMovimento}`);
        } else {
          addAgentMessage('âœ… Dados extraÃ­dos com sucesso!');
        }
      }
    } catch (err) {
      setError('Erro ao processar o arquivo. Verifique se o backend estÃ¡ rodando.');
      console.error('Erro:', err);
      addAgentMessage('âŒ Erro na comunicaÃ§Ã£o com o servidor');
    } finally {
      setLoading(false);
      setAgentProcessing(false);
    }
  };

  const isDataEmpty = (data) => {
    if (!data) return true;
    
    // Verifica se existe QUALQUER dado vÃ¡lido
    const hasAnyData = 
      data.numeroNotaFiscal ||
      data.dataEmissao ||
      data.valorTotal ||
      (data.fornecedor && (data.fornecedor.razaoSocial || data.fornecedor.cnpj)) ||
      (data.cliente && (data.cliente.nome || data.cliente.documento)) ||
      (data.produtos && Array.isArray(data.produtos) && data.produtos.length > 0);

    return !hasAnyData;
  };

  const AgentMessagesPanel = () => {
    const messages = agentMessages || [];
    
    if (messages.length === 0 && !agentProcessing) {
      return null;
    }

    return (
      <div className="agent-messages-panel">
        <h3>ðŸ¤– Agente Inteligente - Processamento</h3>
        <div className="agent-messages-container">
          {messages.map((message) => {
            const messageText = message?.texto || 'Mensagem sem conteÃºdo';
            const hasError = messageText.includes && messageText.includes('âŒ');
            const hasSuccess = messageText.includes && messageText.includes('âœ…');
            
            return (
              <div key={message.id} className={`agent-message ${hasError ? 'error' : ''} ${hasSuccess ? 'success' : ''}`}>
                <span className="agent-message-time">
                  {message.timestamp ? new Date(message.timestamp).toLocaleTimeString() : new Date().toLocaleTimeString()}
                </span>
                <div className="agent-message-text">
                  {messageText.split('\n').map((line, index) => (
                    <div key={index}>{line}</div>
                  ))}
                </div>
              </div>
            );
          })}
          
          {agentProcessing && messages.length === 0 && (
            <div className="agent-message">
              <span className="agent-message-time">{new Date().toLocaleTimeString()}</span>
              <div className="agent-message-text">â³ Iniciando processamento...</div>
            </div>
          )}
          
          {agentProcessing && (
            <div className="agent-processing-indicator">
              <div className="agent-spinner"></div>
              <span>Processando com Agente Inteligente...</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  const formatCurrency = (value) => {
    if (!value && value !== 0) return 'R$ 0,00';
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const formatCNPJ = (cnpj) => {
    if (!cnpj || cnpj === 'N/A' || cnpj === '') return 'N/A';
    const cleaned = cnpj.replace(/\D/g, '');
    if (cleaned.length !== 14) return cnpj;
    return cleaned.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  };

  const formatCPF = (cpf) => {
    if (!cpf || cpf === 'N/A' || cpf === '') return 'N/A';
    const cleaned = cpf.replace(/\D/g, '');
    if (cleaned.length !== 11) return cpf;
    return cleaned.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  const formatEndereco = (endereco) => {
    if (!endereco || endereco === 'N/A' || endereco === '') return 'N/A';
    return endereco;
  };

  const renderDocumentoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') {
      return formatCPF(cliente.cpf);
    } else if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') {
      return formatCNPJ(cliente.cnpj);
    } else if (cliente.documento && cliente.documento !== 'N/A' && cliente.documento !== '') {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return formatCPF(doc);
      if (doc.length === 14) return formatCNPJ(doc);
      return cliente.documento;
    }
    
    return 'N/A';
  };

  const renderProducts = (products) => {
    if (!products || !Array.isArray(products) || products.length === 0) {
      return (
        <div className="products-section">
          <h3>Produtos/ServiÃ§os</h3>
          <p>Nenhum produto encontrado na nota fiscal</p>
        </div>
      );
    }

    return (
      <div className="products-section">
        <h3>Produtos/ServiÃ§os ({products.length})</h3>
        <div className="products-table">
          <table>
            <thead>
              <tr>
                <th>DescriÃ§Ã£o</th>
                <th>Quantidade</th>
                <th>Valor UnitÃ¡rio</th>
                <th>Valor Total</th>
              </tr>
            </thead>
            <tbody>
              {products.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.descricao || produto.nome || 'Produto nÃ£o especificado'}</td>
                  <td className="number">{produto.quantidade ? produto.quantidade.toFixed(2) : '0.00'}</td>
                  <td className="number">{produto.valorUnitario ? formatCurrency(produto.valorUnitario) : 'R$ 0,00'}</td>
                  <td className="number">{produto.valorTotal ? formatCurrency(produto.valorTotal) : 'R$ 0,00'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderParcelas = (parcelas) => {
    if (!parcelas || !Array.isArray(parcelas) || parcelas.length === 0) {
      return null;
    }

    return (
      <div className="parcelas-section">
        <h3>Parcelas ({parcelas.length})</h3>
        <div className="parcelas-table">
          <table>
            <thead>
              <tr>
                <th>Parcela</th>
                <th>Vencimento</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              {parcelas.map((parcela, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{parcela.dataVencimento || parcela.vencimento || 'N/A'}</td>
                  <td className="number">{formatCurrency(parcela.valor)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const getTipoCliente = (cliente) => {
    if (!cliente) return 'N/A';
    
    if (cliente.tipo) return cliente.tipo;
    
    if (cliente.cpf && cliente.cpf !== 'N/A' && cliente.cpf !== '') return 'PF';
    if (cliente.cnpj && cliente.cnpj !== 'N/A' && cliente.cnpj !== '') return 'PJ';
    if (cliente.documento) {
      const doc = cliente.documento.replace(/\D/g, '');
      if (doc.length === 11) return 'PF';
      if (doc.length === 14) return 'PJ';
    }
    
    return 'N/A';
  };

  const renderBasicInfo = (data) => {
    if (!data) return null;

    return (
      <div className="summary-section">
        <h3>InformaÃ§Ãµes da Nota</h3>
        <div className="summary-grid">
          <div className="summary-item">
            <span className="summary-label">NÃºmero NF:</span>
            <span className="summary-value">{data.numeroNotaFiscal || 'N/A'}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Data EmissÃ£o:</span>
            <span className="summary-value">{data.dataEmissao || 'N/A'}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Valor Total:</span>
            <span className="summary-value">{formatCurrency(data.valorTotal)}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Valor Frete:</span>
            <span className="summary-value">{formatCurrency(data.valorFrete)}</span>
          </div>
          <div className="summary-item">
            <span className="summary-label">Produtos:</span>
            <span className="summary-value">{data.produtos?.length || 0} itens</span>
          </div>
          {result?.processamento?.idMovimento && (
            <div className="summary-item">
              <span className="summary-label">ID Movimento:</span>
              <span className="summary-value agent-id">{result.processamento.idMovimento}</span>
            </div>
          )}
        </div>
      </div>
    );
  };

  const handleShowHistory = () => {
    setShowHistory(true);
  };

  const handleCloseHistory = () => {
    setShowHistory(false);
    setSelectedHistoryNote(null);
  };

  const handleSelectHistoryNote = (noteData) => {
    setSelectedHistoryNote(noteData);
    setShowHistory(false);
    if (noteData && noteData.data) {
      setResult({
        success: true,
        data: noteData.data,
        metadata: {
          fileName: noteData.originalFileName,
          processedAt: noteData.processedAt,
          fromHistory: true
        }
      });
    }
  };

  return (
    <div className="invoice-processor">
      <h1>LSR - NF</h1>
      <p>FaÃ§a upload de uma nota fiscal em PDF para extrair os dados automaticamente</p>
      
      <div className="history-button-container">
        <button 
          type="button"
          onClick={handleShowHistory}
          className="history-button"
        >
          ðŸ“‹ Ver HistÃ³rico
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="file-input-container">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
            id="pdfFile"
          />
          <label htmlFor="pdfFile" className="file-label">
            {file ? file.name : 'Selecionar PDF'}
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={loading || !file}
          className="process-button"
        >
          {loading ? 'Processando...' : 'Extrair Dados'}
        </button>
      </form>

      <AgentMessagesPanel />

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {result && result.data && !isDataEmpty(result.data) && (
        <div className="result-container">
          <h2>
            Dados ExtraÃ­dos da Nota Fiscal
            {result.metadata?.fromHistory && <span className="history-indicator">DO HISTÃ“RICO</span>}
            {result.processamento?.processadoPor && (
              <span className="agent-indicator">ðŸ¤– PROCESSADO POR AGENTE INTELIGENTE</span>
            )}
          </h2>
          
          {renderBasicInfo(result.data)}

          {/* Fornecedor */}
          <div className="supplier-section">
            <h3>Fornecedor (Emitente)</h3>
            <div className="supplier-info">
              <p><strong>RazÃ£o Social:</strong> <span className="info-value">{result.data.fornecedor?.razaoSocial || 'N/A'}</span></p>
              <p><strong>Nome Fantasia:</strong> <span className="info-value">{result.data.fornecedor?.fantasia || 'N/A'}</span></p>
              <p><strong>CNPJ:</strong> <span className="info-value">{formatCNPJ(result.data.fornecedor?.cnpj) || 'N/A'}</span></p>
              <p><strong>EndereÃ§o:</strong> <span className="address-line">{formatEndereco(result.data.fornecedor?.endereco)}</span></p>
            </div>
          </div>

          {/* Cliente */}
          <div className="client-section">
            <h3>Cliente (DestinatÃ¡rio)</h3>
            <div className="client-info">
              <p><strong>Nome:</strong> <span className="info-value">{result.data.cliente?.nome || 'N/A'}</span></p>
              <p><strong>Documento:</strong> <span className="info-value">{renderDocumentoCliente(result.data.cliente)}</span></p>
              <p><strong>Tipo:</strong> <span className="info-value">{getTipoCliente(result.data.cliente)}</span></p>
              <p><strong>EndereÃ§o:</strong> <span className="address-line">{formatEndereco(result.data.cliente?.endereco)}</span></p>
            </div>
          </div>

          {renderProducts(result.data.produtos)}
          {renderParcelas(result.data.parcelas)}

          {/* ClassificaÃ§Ã£o */}
          <div className="classification-section">
            <h3>ClassificaÃ§Ã£o da Despesa</h3>
            <div className="classification-info">
              <p><strong>Categoria:</strong> <span className="info-value">
                {result.data.classificacaoDespesa && result.data.classificacaoDespesa.length > 0 
                  ? result.data.classificacaoDespesa.join(', ') 
                  : 'NÃ£o classificada'}
              </span></p>
            </div>
          </div>

          {/* JSON Completo para debug */}
          <details className="json-details">
            <summary>JSON Completo (Debug)</summary>
            <pre className="result-json">
              {JSON.stringify(result, null, 2)}
            </pre>
          </details>
        </div>
      )}

      <HistoryPanel 
        isOpen={showHistory}
        onClose={handleCloseHistory}
        onSelectNote={handleSelectHistoryNote}
      />
    </div>
  );
};

export default InvoiceProcessor;
